CREATE OR REPLACE PROCEDURE pls_obter_cadastro_pagador ( nr_sequencia_p bigint, nr_seq_pagador_intercambio_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, nm_usuario_p text, qt_pagador_p INOUT bigint, ie_pf_pj_p INOUT text) AS $body$
DECLARE


qt_pagador_w	bigint;
ie_pf_pj_w	varchar(14);

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_seq_pagador_intercambio_p IS NOT NULL AND nr_seq_pagador_intercambio_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	begin
	select	coalesce(count(*),0)
	into STRICT	qt_pagador_w
	from	pls_contrato_pagador
	where	ie_tipo_pagador = 'P'
	and	nr_sequencia <>  nr_sequencia_p
	and	nr_seq_pagador_intercambio = nr_seq_pagador_intercambio_p;
	exception
		when others then
		qt_pagador_w := 0;
	end;

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		begin
		begin
			select	coalesce(cd_pessoa_fisica,'N')
			into STRICT	ie_pf_pj_w
			from	pls_contrato_pagador
			where	nr_sequencia <> nr_sequencia_p
			and	nr_seq_pagador_intercambio = nr_seq_pagador_intercambio_p
			and	cd_pessoa_fisica = cd_pessoa_fisica_p;
			exception
				when others then
				ie_pf_pj_w := 'N';
			end;
		end;
	elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
		begin
		select	coalesce(cd_cgc, 'N')
		into STRICT	ie_pf_pj_w
		from	pls_contrato_pagador
		where	nr_sequencia <> nr_sequencia_p
		and	nr_seq_pagador_intercambio = nr_seq_pagador_intercambio_p
		and	cd_cgc = cd_cgc_p;
		exception
			when others then
			ie_pf_pj_w := 'N';
		end;
	end if;
	end;
end if;
commit;
qt_pagador_p	:= qt_pagador_w;
ie_pf_pj_p	:= ie_pf_pj_w;
end;
$body$
