SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_obter_caminho_arquivo_fat (nr_seq_fatura_p pls_fatura.nr_sequencia%type, ds_hash_p text, ie_tipo_interface_p pls_regra_arquivo_fatura.ie_tipo_interface%type, nm_usuario_p usuario.nm_usuario%type, ds_caminho_arquivo_p INOUT pls_regra_arquivo_fatura.ds_caminho_arquivo%type, ie_tipo_arquivo_p INOUT pls_regra_arquivo_fatura.ie_tipo_arquivo%type, cd_interface_p INOUT pls_regra_arquivo_fatura.cd_interface%type, ie_compactar_p INOUT pls_regra_arquivo_fatura.ie_compactar%type, nr_seq_esquema_xml_p INOUT pls_regra_arquivo_fatura.nr_seq_esquema_xml%type, nm_arquivo_p INOUT pls_regra_arquivo_fatura.nm_arquivo%type, nr_seq_regra_p INOUT pls_regra_arquivo_fatura.nr_sequencia%type, ds_mascara_p INOUT pls_regra_arquivo_fatura.nm_mascara_arquivo%type, ds_mascara_zip_p INOUT pls_regra_arquivo_fatura.nm_mascara_arquivo_zip%type, ds_interface_p INOUT pls_regra_arquivo_fatura.ds_interface%type, ie_tipo_compactar_p INOUT pls_regra_arquivo_fatura.ie_tipo_compactar%type, ds_mascara_adic_p INOUT pls_regra_arquivo_fatura.nm_mascara_arquivo_adic%type) AS $body$
DECLARE

					
ie_ptu_vigente_w		varchar(1);
ie_tipo_faturamento_w		varchar(2);
ie_tipo_arquivo_w		varchar(3);
cd_cooperativa_w		varchar(10);
nm_arquivo_w			varchar(255);
ds_caminho_arquivo_w		varchar(255);
cd_interface_w			integer;
ie_operadora_w			integer;
ie_cooperativa_w		integer;
nr_seq_cooperativa_w		bigint;
nr_seq_pagador_w		bigint;
nr_seq_contrato_w		bigint;
nr_seq_camara_w			bigint;
nr_contrato_w			bigint;
nr_seq_lote_w			bigint;
nr_titulo_w			bigint;
nr_seq_intercambio_w		bigint;
cd_operadora_empresa_w		bigint;
nr_seq_regra_fat_w		bigint;
					
C01 CURSOR FOR
	SELECT	a.ds_caminho_arquivo,
		a.ie_ptu_vigente,
		a.ie_tipo_arquivo,
		a.cd_interface,
		a.ie_compactar,
		a.nr_seq_esquema_xml,
		a.nm_arquivo,
		a.nr_sequencia,
		a.nm_mascara_arquivo,
		a.nm_mascara_arquivo_zip,
		a.ds_interface,
		coalesce(a.ie_tipo_compactar, 'A') ie_tipo_compactar,
		a.nm_mascara_arquivo_adic
	from	pls_regra_arquivo_fatura a
	where (a.ie_tipo_faturamento	= ie_tipo_faturamento_w 	or coalesce(a.ie_tipo_faturamento::text, '') = '')
	and (a.nr_seq_cooperativa 	= nr_seq_cooperativa_w		or coalesce(a.nr_seq_cooperativa::text, '') = '')
	and (a.nr_seq_contrato 	= nr_seq_contrato_w		or coalesce(a.nr_seq_contrato::text, '') = '')
	and (a.cd_operadora_empresa = cd_operadora_empresa_w	or coalesce(a.cd_operadora_empresa::text, '') = '')
	and (a.nr_seq_regra_fat	= nr_seq_regra_fat_w		or coalesce(a.nr_seq_regra_fat::text, '') = '')
	and (a.ie_tipo_interface	= ie_tipo_interface_p)
	order by
		coalesce(a.cd_operadora_empresa,0),
		coalesce(a.nr_seq_contrato,0),
		coalesce(a.nr_seq_cooperativa,0),
		coalesce(a.nr_seq_regra_fat,0),
		coalesce(a.ie_tipo_faturamento,0);
		

BEGIN
if (nr_seq_fatura_p IS NOT NULL AND nr_seq_fatura_p::text <> '') then
	select	nr_seq_congenere,
		nr_seq_pagador,
		nr_seq_lote
	into STRICT	nr_seq_cooperativa_w,
		nr_seq_pagador_w,
		nr_seq_lote_w
	from	pls_fatura
	where	nr_sequencia = nr_seq_fatura_p;
	
	select	max(nr_seq_regra_fat)
	into STRICT	nr_seq_regra_fat_w
	from	pls_lote_faturamento
	where	nr_sequencia = nr_seq_lote_w;
	
	select	max(nr_seq_contrato),
		max(nr_seq_intercambio),
		max(cd_operadora_empresa)
	into STRICT	nr_seq_contrato_w,
		nr_seq_intercambio_w,
		cd_operadora_empresa_w
	from	pls_segurado
	where	nr_seq_pagador = nr_seq_pagador_w;
	
	if (coalesce(cd_operadora_empresa_w::text, '') = '') then
		select	max(cd_operadora_empresa)
		into STRICT	cd_operadora_empresa_w
		from	pls_contrato
		where	nr_sequencia	= nr_seq_contrato_w;
	end if;
	
	ie_tipo_faturamento_w	:= 'P'; /* Padrão pós */
	
	if (nr_seq_intercambio_w IS NOT NULL AND nr_seq_intercambio_w::text <> '') then
		select	count(1)
		into STRICT	ie_cooperativa_w
		from	pls_intercambio
		where	nr_sequencia = nr_seq_intercambio_w
		and	(nr_seq_congenere IS NOT NULL AND nr_seq_congenere::text <> '');
		
		if (ie_cooperativa_w = 0) then
			select	count(1)
			into STRICT	ie_operadora_w
			from	pls_intercambio
			where	nr_sequencia = nr_seq_intercambio_w
			and	(nr_seq_oper_congenere IS NOT NULL AND nr_seq_oper_congenere::text <> '');
			
			if (ie_operadora_w > 0) then
				ie_tipo_faturamento_w := 'PO'; /* Operadoras congêneres */
			end if;
		else
			ie_tipo_faturamento_w := 'PI'; /* Cooperativas médicas */
		end if;
	end if;
	
	if (nr_seq_pagador_w IS NOT NULL AND nr_seq_pagador_w::text <> '') and (nr_seq_cooperativa_w IS NOT NULL AND nr_seq_cooperativa_w::text <> '') then		
		ie_tipo_faturamento_w	:= 'IC';
	end if;
	
	open C01;
	loop
	fetch C01 into
		ds_caminho_arquivo_w,
		ie_ptu_vigente_w,
		ie_tipo_arquivo_w,
		cd_interface_w,
		ie_compactar_p,
		nr_seq_esquema_xml_p,
		nm_arquivo_p,
		nr_seq_regra_p,
		ds_mascara_p,
		ds_mascara_zip_p,
		ds_interface_p,
		ie_tipo_compactar_p,
		ds_mascara_adic_p;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
	
	if (ie_ptu_vigente_w = 'S') then
		cd_interface_w 		:= '';
		ie_tipo_arquivo_w	:= 'TXT';
	end if;
	
	if (nr_seq_cooperativa_w IS NOT NULL AND nr_seq_cooperativa_w::text <> '') then
		select	cd_cooperativa
		into STRICT	cd_cooperativa_w
		from	pls_congenere
		where	nr_sequencia = nr_seq_cooperativa_w;
	end if;
	
	if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
		select	nr_contrato
		into STRICT	nr_contrato_w
		from	pls_contrato
		where	nr_sequencia = nr_seq_contrato_w;
	end if;
	
	if (substr(ds_caminho_arquivo_w,length(ds_caminho_arquivo_w)-1,length(ds_caminho_arquivo_w)) = '\') or (substr(ds_caminho_arquivo_w,length(ds_caminho_arquivo_w)-1,length(ds_caminho_arquivo_w)) = '/')then
		ds_caminho_arquivo_w	:= substr(ds_caminho_arquivo_w,1,length(ds_caminho_arquivo_w)-1);
	end if;

	ds_caminho_arquivo_w	:= substr(replace_macro(ds_caminho_arquivo_w,'@CD_COOPERATIVA',cd_cooperativa_w),1,255);
	ds_caminho_arquivo_w	:= substr(replace_macro(ds_caminho_arquivo_w,'@NR_CONTRATO',nr_contrato_w),1,255);
	ds_caminho_arquivo_w	:= substr(replace_macro(ds_caminho_arquivo_w,'@ANO', to_char(clock_timestamp(),'yyyy')),1,255);
	ds_caminho_arquivo_w	:= substr(replace_macro(ds_caminho_arquivo_w,'@MES', to_char(clock_timestamp(),'mm')),1,255);
	ds_caminho_arquivo_w	:= substr(replace_macro(ds_caminho_arquivo_w,'@DIA', to_char(clock_timestamp(),'dd')),1,255);
	
	ds_caminho_arquivo_p	:= ds_caminho_arquivo_w;
	cd_interface_p 		:= cd_interface_w;
	ie_tipo_arquivo_p	:= ie_tipo_arquivo_w;
end if;
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obter_caminho_arquivo_fat (nr_seq_fatura_p pls_fatura.nr_sequencia%type, ds_hash_p text, ie_tipo_interface_p pls_regra_arquivo_fatura.ie_tipo_interface%type, nm_usuario_p usuario.nm_usuario%type, ds_caminho_arquivo_p INOUT pls_regra_arquivo_fatura.ds_caminho_arquivo%type, ie_tipo_arquivo_p INOUT pls_regra_arquivo_fatura.ie_tipo_arquivo%type, cd_interface_p INOUT pls_regra_arquivo_fatura.cd_interface%type, ie_compactar_p INOUT pls_regra_arquivo_fatura.ie_compactar%type, nr_seq_esquema_xml_p INOUT pls_regra_arquivo_fatura.nr_seq_esquema_xml%type, nm_arquivo_p INOUT pls_regra_arquivo_fatura.nm_arquivo%type, nr_seq_regra_p INOUT pls_regra_arquivo_fatura.nr_sequencia%type, ds_mascara_p INOUT pls_regra_arquivo_fatura.nm_mascara_arquivo%type, ds_mascara_zip_p INOUT pls_regra_arquivo_fatura.nm_mascara_arquivo_zip%type, ds_interface_p INOUT pls_regra_arquivo_fatura.ds_interface%type, ie_tipo_compactar_p INOUT pls_regra_arquivo_fatura.ie_tipo_compactar%type, ds_mascara_adic_p INOUT pls_regra_arquivo_fatura.nm_mascara_arquivo_adic%type) FROM PUBLIC;

