CREATE OR REPLACE PROCEDURE pls_obter_canc_item_orig ( nr_seq_conta_proc_p bigint, nr_seq_regra_p INOUT bigint) AS $body$
DECLARE


nr_seq_conta_w			bigint;
nr_seq_regra_w			bigint;
dt_procedimento_w		timestamp;
dt_referencia_w			timestamp;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_regra_canc_item_orig	a
	where	dt_referencia_w	between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia, dt_referencia_w)
	order by
		a.ie_cancelamento;


BEGIN
select	max(a.dt_procedimento),
	max(a.nr_seq_conta)
into STRICT	dt_procedimento_w,
	nr_seq_conta_w
from	pls_conta_proc	a
where	a.nr_sequencia	= nr_seq_conta_proc_p;

if (dt_procedimento_w IS NOT NULL AND dt_procedimento_w::text <> '') then
	dt_referencia_w	:= dt_procedimento_w;
else
	select	max(a.dt_emissao)
	into STRICT	dt_referencia_w
	from	pls_conta	a
	where	a.nr_sequencia	= nr_seq_conta_w;
end if;

open C01;
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

nr_seq_regra_p	:= nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
