CREATE OR REPLACE PROCEDURE pls_obter_carteira_cobran_ted ( cd_estabelecimento_p bigint, vl_referencia_p bigint, dt_referencia_p timestamp, nr_seq_conta_banco_p INOUT bigint, nr_seq_carteira_p INOUT bigint) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter a carteira e conta bancária da cobrança TED
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_seq_carteira_w		bigint;
nr_seq_conta_banco_w	bigint;

c01 CURSOR FOR
	SELECT	nr_seq_carteira,
		nr_seq_conta_banco
	from	pls_regra_carteira_ted
	where	coalesce(vl_referencia_p,0) between coalesce(vl_minimo,0) and coalesce(vl_maximo,9999999)
	and	coalesce(dt_referencia_p,clock_timestamp()) between coalesce(dt_inicio_vigencia,to_date('01/01/1900','dd/mm/yyyy'))
				and coalesce(dt_fim_vigencia,to_date('31/12/2300','dd/mm/yyyy'))
	and	cd_estabelecimento	= cd_estabelecimento_p;


BEGIN
nr_seq_carteira_w		:= null;
nr_seq_conta_banco_w	:= nr_seq_conta_banco_p;

open c01;
loop fetch c01 into
	nr_seq_carteira_w,
	nr_seq_conta_banco_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

nr_seq_carteira_p		:= nr_seq_carteira_w;
nr_seq_conta_banco_p	:= nr_seq_conta_banco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
