SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_obter_cd_pf_cgc_pagador ( nr_seq_intercambio_p bigint, nm_usuario_p text) AS $body$
DECLARE


		
nr_seq_congenere_w	bigint;
nr_seq_oper_congenere_w	bigint;
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);
ie_tipo_pagador_w	varchar(1);
nr_seq_pagador_w	bigint;
cd_cgc_pagador_fund_w	varchar(14);
ie_endereco_boleto_w	varchar(3);
qt_pagador_w		bigint;
qt_pagador_ww		bigint;
ie_tipo_contrato_w	varchar(10);


BEGIN

select	nr_seq_congenere,
	nr_seq_oper_congenere,
	cd_pessoa_fisica,
	cd_cgc,
	ie_tipo_contrato
into STRICT	nr_seq_congenere_w,
	nr_seq_oper_congenere_w,
	cd_pessoa_fisica_w,
	cd_cgc_w,
	ie_tipo_contrato_w
from	pls_intercambio
where	nr_sequencia	= nr_seq_intercambio_p;

if (nr_seq_congenere_w IS NOT NULL AND nr_seq_congenere_w::text <> '') then
	select	max(cd_cgc)
	into STRICT	cd_cgc_pagador_fund_w
	from	pls_congenere
	where	nr_sequencia	= nr_seq_congenere_w;
elsif (nr_seq_oper_congenere_w IS NOT NULL AND nr_seq_oper_congenere_w::text <> '') then
	select	max(cd_cgc)
	into STRICT	cd_cgc_pagador_fund_w
	from	pls_congenere
	where	nr_sequencia	= nr_seq_oper_congenere_w;
end if;	

select	count(*)
into STRICT	qt_pagador_w
from	pls_contrato_pagador
where	nr_seq_pagador_intercambio	= nr_seq_intercambio_p;

if (qt_pagador_w = 0) then
	ie_tipo_pagador_w	:= 'P';
elsif (qt_pagador_w > 0) then
	ie_tipo_pagador_w	:= 'S';
end if;

select	CASE WHEN cd_pessoa_fisica_w='' THEN 'PJC'  ELSE 'PFR' END
into STRICT	ie_endereco_boleto_w
;

if (ie_tipo_contrato_w	= 'F') then
	select	count(*)
	into STRICT	qt_pagador_ww
	from	pls_contrato_pagador
	where	nr_seq_pagador_intercambio	= nr_seq_intercambio_p
	and	((cd_pessoa_fisica		= cd_pessoa_fisica_w)
	or (cd_cgc				= cd_cgc_w));
	
	if (qt_pagador_ww = 0) then
		select	nextval('pls_contrato_pagador_seq')
		into STRICT	nr_seq_pagador_w
		;
		
		insert into	pls_contrato_pagador(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_pagador_intercambio,cd_cgc,cd_pessoa_fisica,ie_tipo_pagador,ie_endereco_boleto,
				ie_envia_cobranca,ie_calc_primeira_mens,ie_calculo_proporcional,ie_notificacao,ie_taxa_emissao_boleto, ie_receber_sms)
		values (	nr_seq_pagador_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_intercambio_p,cd_cgc_w,cd_pessoa_fisica_w,ie_tipo_pagador_w,ie_endereco_boleto_w,
				'A','I','M','NR','N', 'S');
				
		insert into	pls_contrato_pagador(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_pagador_compl,cd_cgc,ie_tipo_pagador,ie_endereco_boleto,ie_envia_cobranca,
				ie_calc_primeira_mens,ie_calculo_proporcional,ie_notificacao,ie_taxa_emissao_boleto,ie_finalidade, ie_receber_sms)
		values (	nextval('pls_contrato_pagador_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_pagador_w,cd_cgc_pagador_fund_w,'S','PJC','A',
				'I','M','NR','N','M', 'S');
	end if;			
elsif (ie_tipo_contrato_w in ('C','S','I')) then
	select	count(*)
	into STRICT	qt_pagador_ww
	from	pls_contrato_pagador
	where	nr_seq_pagador_intercambio	= nr_seq_intercambio_p
	and	cd_cgc				= cd_cgc_pagador_fund_w;
	
	if (qt_pagador_ww	= 0) then
		insert into	pls_contrato_pagador(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_pagador_intercambio,cd_cgc,ie_tipo_pagador,ie_endereco_boleto,ie_envia_cobranca,
				ie_calc_primeira_mens,ie_calculo_proporcional,ie_notificacao,ie_taxa_emissao_boleto, ie_receber_sms)
		values (	nextval('pls_contrato_pagador_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_intercambio_p,cd_cgc_pagador_fund_w,ie_tipo_pagador_w,ie_endereco_boleto_w,'A',
				'I','M','NR','N', 'S');
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
