CREATE OR REPLACE PROCEDURE pls_obter_cod_barras_js ( cd_usuario_plano_p text, nm_beneficiario_p INOUT text, nr_via_p INOUT text, nr_seq_segurado_p INOUT text, dt_validade_p INOUT text, nr_digito_p INOUT text, cd_pessoa_fisica_p INOUT text, dt_contratacao_p INOUT text, ds_motivo_cancelam_p INOUT text, dt_rescisao_p INOUT text, ds_abrengencia_p INOUT text, DS_COOPERATIVA_P INOUT text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
dt_contratacao_w		timestamp;
ds_motivo_cancelamento_w	varchar(255);
dt_rescisao_w			timestamp;


BEGIN 
 
SELECT * FROM pls_obter_dados_codigo_barras(	cd_usuario_plano_p, nm_beneficiario_p, nr_via_p, nr_seq_segurado_p, dt_validade_p, nr_digito_p, cd_pessoa_fisica_p, cd_estabelecimento_p, nm_usuario_p) INTO STRICT nm_beneficiario_p, nr_via_p, nr_seq_segurado_p, dt_validade_p, nr_digito_p, cd_pessoa_fisica_p;
				 
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then 
	select	dt_contratacao, 
		DT_RESCISAO, 
		substr(pls_obter_desc_motivo_cancel(nr_seq_motivo_cancelamento),1,255) 
	into STRICT	dt_contratacao_w, 
		dt_rescisao_w, 
		ds_motivo_cancelamento_w 
	from	pls_segurado 
	where	nr_sequencia	= (nr_seq_segurado_p)::numeric;
	 
	ds_abrengencia_p	:= substr(pls_obter_abrangencia_resumida((nr_seq_segurado_p)::numeric ),1,255);
	DS_COOPERATIVA_P := PLS_CONSISTIR_BENEF_REPASSE((nr_seq_segurado_p)::numeric , DS_COOPERATIVA_P);
end if;
 
dt_contratacao_p	:= to_char(dt_contratacao_w,'dd/mm/yyyy');
ds_motivo_cancelam_p	:= ds_motivo_cancelamento_w;
dt_rescisao_p		:= to_char(dt_rescisao_w,'dd/mm/yyyy');
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
