CREATE OR REPLACE PROCEDURE pls_obter_conta_contab_eve_fin ( nr_seq_evento_movimento_p bigint, cd_conta_cred_p INOUT text) AS $body$
DECLARE


cd_conta_contabil_w		varchar(20);
nr_seq_prestador_w		bigint;
nr_seq_evento_w			bigint;
dt_movimento_w			timestamp;

C01 CURSOR FOR
	SELECT	cd_conta_cred
	from	pls_regra_contab_eve_fin
	where	((nr_seq_prestador	= nr_seq_prestador_w) or (coalesce(nr_seq_prestador::text, '') = ''))
	and	((nr_seq_evento		= nr_seq_evento_w) or (coalesce(nr_seq_evento::text, '') = ''))
	and	dt_movimento_w between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_movimento_w)
	order by
		coalesce(nr_seq_evento,0),
		coalesce(nr_seq_prestador,0);


BEGIN
select	dt_movimento,
	nr_seq_prestador,
	nr_seq_evento
into STRICT	dt_movimento_w,
	nr_seq_prestador_w,
	nr_seq_evento_w
from	pls_evento_movimento
where	nr_sequencia	= nr_seq_evento_movimento_p;

open C01;
loop
fetch C01 into
	cd_conta_contabil_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

cd_conta_cred_p	:= cd_conta_contabil_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
