CREATE OR REPLACE PROCEDURE pls_obter_cor_atend_corrente ( ds_lista_tempo_p text, ds_regra_p INOUT text, ds_cor_p INOUT text, qt_tempo_maximo_p INOUT bigint) AS $body$
DECLARE

ds_lista_tempo_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_tempo_w		bigint;
ds_regra_w		varchar(100);
ds_cor_w		varchar(4000);
qt_tempo_maximo_w	bigint;

BEGIN
if (ds_lista_tempo_p IS NOT NULL AND ds_lista_tempo_p::text <> '') then
	begin
	ds_lista_tempo_w	:= ds_lista_tempo_p;
	while(ds_lista_tempo_w IS NOT NULL AND ds_lista_tempo_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in ds_lista_tempo_w);
		if (nr_pos_virgula_w > 0) then
			begin
			nr_tempo_w	:= (substr(ds_lista_tempo_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_tempo_w	:= substr(ds_lista_tempo_w,nr_pos_virgula_w+1,length(ds_lista_tempo_w));
			end;
		else
			begin
			nr_tempo_w	:= (ds_lista_tempo_w)::numeric;
			ds_lista_tempo_w	:= null;
			end;
		end if;
		if (nr_tempo_w > 0) then
			begin
			SELECT * FROM pls_obter_atend_regra_tempo(
				nr_tempo_w, ds_regra_w, ds_cor_w, qt_tempo_maximo_w) INTO STRICT ds_regra_w, ds_cor_w, qt_tempo_maximo_w;
				ds_cor_w := ds_cor_w || ',';
			end;
		end if;
		end;
	end loop;
	ds_cor_p		:= ds_cor_w;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
