CREATE OR REPLACE PROCEDURE pls_obter_dados_atend_regra ( nr_seq_regra_tempo_p bigint, cd_pessoa_p text, ie_tipo_pessoa_p text, ie_tipo_pessoa_atend_p text, nr_seq_congenere_p bigint, ds_regra_tempo_p INOUT text, ds_cor_regra_tempo_p INOUT text, qt_pendencia_p INOUT bigint, nm_pessoa_p INOUT text, ds_tipo_pessoa_p INOUT text) AS $body$
BEGIN
 
if (nr_seq_regra_tempo_p IS NOT NULL AND nr_seq_regra_tempo_p::text <> '') then 
	begin 
		select	ds_regra, 
			ds_cor 
		into STRICT	ds_regra_tempo_p, 
			ds_cor_regra_tempo_p 
		from	pls_atend_regra_tempo 
		where	nr_sequencia	= nr_seq_regra_tempo_p;
	end;
end if;
 
if (ie_tipo_pessoa_p IS NOT NULL AND ie_tipo_pessoa_p::text <> '') then 
	if (cd_pessoa_p IS NOT NULL AND cd_pessoa_p::text <> '') then 
		select	coalesce(substr(pls_obter_pendencia_atend(cd_pessoa_p, ie_tipo_pessoa_p, null), 1, 10), 0) 
		into STRICT	qt_pendencia_p 
		;
	end if;	
 
	if (nr_seq_congenere_p IS NOT NULL AND nr_seq_congenere_p::text <> '') then 
		select	substr(pls_obter_nome_congenere(nr_seq_congenere_p), 1, 255) 
		into STRICT	nm_pessoa_p 
		;
	elsif (ie_tipo_pessoa_p = 'PF') then 
		select	substr(obter_nome_pf_pj(cd_pessoa_p, null), 1, 255) 
		into STRICT	nm_pessoa_p 
		;
	elsif (ie_tipo_pessoa_p = 'PJ') then 
		select	substr(obter_nome_pf_pj(null, cd_pessoa_p), 1, 255) 
		into STRICT	nm_pessoa_p 
		;	
	end if;
end if;
 
select	substr(obter_valor_dominio(3181, ie_tipo_pessoa_atend_p), 1, 255) 
into STRICT	ds_tipo_pessoa_p
;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
