CREATE OR REPLACE PROCEDURE pls_obter_dados_codigo_barras ( cd_usuario_plano_p text, nm_beneficiario_p INOUT text, nr_via_p INOUT text, nr_seq_segurado_p INOUT text, dt_validade_p INOUT text, nr_digito_p INOUT text, cd_pessoa_fisica_p INOUT text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_beneficiario_w		varchar(320);
nr_via_w			varchar(350);
dt_validade_w			varchar(20);
dt_limite_utilizacao_w		varchar(20);
nr_seq_segurado_w		varchar(1000);
nr_digito_w			smallint;
cd_pessoa_fisica_w		bigint;
ie_restringe_estab_w		varchar(1);


BEGIN

ie_restringe_estab_w	:= coalesce(obter_valor_param_usuario(1273, 8, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p),'N');

select	Calcula_digito('MODULO11',cd_usuario_plano_p)
into STRICT	nr_digito_w
;

nr_digito_p	:= nr_digito_w;

SELECT * FROM pls_obter_benef_tarja(cd_usuario_plano_p, ie_restringe_estab_w, nm_usuario_p, cd_estabelecimento_p, cd_pessoa_fisica_w, nr_seq_segurado_w) INTO STRICT cd_pessoa_fisica_w, nr_seq_segurado_w;

begin
select	substr(tws_get_name_person(a.cd_pessoa_fisica, a.cd_estabelecimento),1,250),
	to_char(b.dt_validade_carteira,'dd/mm/yyyy'),
	b.nr_via_solicitacao,
	to_char(a.dt_limite_utilizacao,'dd/mm/yyyy')
into STRICT	nm_beneficiario_w,
	dt_validade_w,
	nr_via_w,
	dt_limite_utilizacao_w
from	pls_segurado		a,
	pls_segurado_carteira	b
where	b.nr_seq_segurado	= a.nr_sequencia
and	a.nr_sequencia		= nr_seq_segurado_w;
exception
when others then
	nm_beneficiario_w	:= '';
	dt_validade_w		:= null;
	nr_via_w		:= null;
	dt_limite_utilizacao_w	:= null;
end;

nm_beneficiario_p	:= nm_beneficiario_w;
nr_seq_segurado_p	:= nr_seq_segurado_w;
nr_via_p		:= nr_via_w;
cd_pessoa_fisica_p := cd_pessoa_fisica_w;

if (to_date(dt_validade_w, 'dd/mm/yy') < to_date(dt_limite_utilizacao_w, 'dd/mm/yy')) then
	dt_validade_p	:= dt_validade_w;
else
	begin
	if (dt_limite_utilizacao_w IS NOT NULL AND dt_limite_utilizacao_w::text <> '') then
		dt_validade_p	:= dt_limite_utilizacao_w;
	else
		dt_validade_p	:= dt_validade_w;
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
