CREATE OR REPLACE PROCEDURE pls_obter_dados_guia_web ( nr_seq_guia_p bigint, cd_senha_p INOUT text, dt_validade_senha_p INOUT text, ds_observacao_p INOUT text, qt_dia_autorizado_p INOUT bigint) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter os dados da senha da autorização no portal web
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/cd_senha_w		varchar(20)	:= '';
dt_validade_senha_w	varchar(15)	:= '';
ds_observacao_w		varchar(255)	:= '';
qt_dia_autorizado_w	smallint	:= 0;



BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then

	select	cd_senha,
		to_char(dt_validade_senha, 'dd/mm/yyyy') dt_validade_senha,
		substr(ds_observacao,0,250) ds_observacao,
		qt_dia_autorizado
	into STRICT	cd_senha_w,
		dt_validade_senha_w,
		ds_observacao_w,
		qt_dia_autorizado_w
        from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;
end if;

cd_senha_p		:= cd_senha_w;
dt_validade_senha_p	:= dt_validade_senha_w;
ds_observacao_p		:= ds_observacao_w;
qt_dia_autorizado_p	:= qt_dia_autorizado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
