CREATE OR REPLACE PROCEDURE pls_obter_dados_internado ( nr_chave_p bigint, ie_tipo_p text, nr_seq_segurado_p INOUT bigint, nm_segurado_p INOUT text, cd_guia_referencia_p INOUT text, dt_alta_p INOUT timestamp, dt_alta_prevista_p INOUT timestamp ) AS $body$
BEGIN
if (ie_tipo_p = 'B') then 
	select 	max(x.nr_seq_segurado), 
		pls_obter_dados_segurado(max(x.nr_seq_segurado),'N') nm_segurado, 
		max(x.cd_guia_referencia),   
		max(x.dt_alta), 
		max(x.dt_alta_estimado) 
	into STRICT 	nr_seq_segurado_p, 
		nm_segurado_p, 
		cd_guia_referencia_p, 
		dt_alta_p, 
		dt_alta_prevista_p 
	from 	pls_segurado a, 
		table(pls_cta_beneficiario_pck.obter_se_internado(a.nr_sequencia,trunc(clock_timestamp(),'dd'))) x 
	where 	x.nr_seq_segurado = a.nr_sequencia 
	and 	a.nr_sequencia = nr_chave_p;
elsif (ie_tipo_p = 'P') then 
	select max(x.nr_seq_segurado), 
		pls_obter_dados_segurado(max(x.nr_seq_segurado),'N') nm_segurado, 
		max(x.cd_guia_referencia),   
		max(x.dt_alta), 
		max(x.dt_alta_estimado) 
	into STRICT 	nr_seq_segurado_p, 
		nm_segurado_p, 
		cd_guia_referencia_p, 
		dt_alta_p, 
		dt_alta_prevista_p 
	from 	pls_segurado a, 
		table(pls_cta_beneficiario_pck.obter_se_internado(a.nr_sequencia,trunc(clock_timestamp(),'dd'))) x 
	where 	x.nr_seq_segurado = a.nr_sequencia 
	and 	a.nr_seq_pagador = nr_chave_p;	
elsif (ie_tipo_p = 'S') then 
	select max(x.nr_seq_segurado), 
		pls_obter_dados_segurado(max(x.nr_seq_segurado),'N') nm_segurado, 
		max(x.cd_guia_referencia),   
		max(x.dt_alta), 
		max(x.dt_alta_estimado) 
	into STRICT 	nr_seq_segurado_p, 
		nm_segurado_p, 
		cd_guia_referencia_p, 
		dt_alta_p, 
		dt_alta_prevista_p 
	from 	pls_segurado a, 
		table(pls_cta_beneficiario_pck.obter_se_internado(a.nr_sequencia,trunc(clock_timestamp(),'dd'))) x 
	where 	x.nr_seq_segurado = a.nr_sequencia 
	and 	a.nr_seq_subestipulante = nr_chave_p;	
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
