CREATE OR REPLACE PROCEDURE pls_obter_dados_material_web ( nr_seq_material_p bigint, cd_material_ops_p text, ie_opcao_p text, ds_retorno_p INOUT text, cd_retorno_p INOUT text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter dados do produto
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

IE_OPCAO_P

NC - Nome comercial
N - Nome

*/ds_retorno_w		varchar(255);
nr_seq_codigo_w		varchar(255);


BEGIN

if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then

	if (ie_opcao_p 	= 'NC') then
		select 	max(coalesce(ds_nome_comercial,ds_material))
		into STRICT	ds_retorno_w
		from	pls_material
		where 	nr_sequencia  = nr_seq_material_p
		and 	ie_situacao = 'A'
		and (coalesce(dt_exclusao::text, '') = '' or dt_exclusao > clock_timestamp());
	elsif (ie_opcao_p 	= 'N') then
		select 	max(ds_material)
		into STRICT	ds_retorno_w
		from	pls_material
		where 	nr_sequencia  = nr_seq_material_p
		and 	ie_situacao = 'A'
		and (coalesce(dt_exclusao::text, '') = '' or dt_exclusao > clock_timestamp());
	end if;

	select	max(cd_material_ops)
	into STRICT	nr_seq_codigo_w
	from	pls_material
	where	nr_sequencia	= nr_seq_material_p;

elsif (cd_material_ops_p IS NOT NULL AND cd_material_ops_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_codigo_w
	from	pls_material
	where	cd_material_ops	= cd_material_ops_p;

	if (ie_opcao_p 	= 'NC') then
		select 	max(coalesce(ds_nome_comercial,ds_material))
		into STRICT	ds_retorno_w
		from	pls_material
		where 	nr_sequencia  = nr_seq_codigo_w
		and 	ie_situacao = 'A'
		and (coalesce(dt_exclusao::text, '') = '' or dt_exclusao > clock_timestamp());
	elsif (ie_opcao_p 	= 'N') then
		select 	max(ds_material)
		into STRICT	ds_retorno_w
		from	pls_material
		where 	nr_sequencia  = nr_seq_codigo_w
		and 	ie_situacao = 'A'
		and (coalesce(dt_exclusao::text, '') = '' or dt_exclusao > clock_timestamp());
	end if;

end if;

ds_retorno_p := ds_retorno_w;
cd_retorno_p := nr_seq_codigo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
