CREATE OR REPLACE PROCEDURE pls_obter_dados_novo_benef ( ie_tipo_proposta_p bigint, nr_seq_proposta_p bigint, nr_seq_contrato_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_plano_p INOUT bigint, nr_seq_tabela_p INOUT bigint, nr_sequencia_p INOUT bigint, nr_seq_motivo_inclusao_p INOUT bigint, nr_seq_titular_contrato_p INOUT bigint, dt_contratacao_p INOUT timestamp ) AS $body$
DECLARE

 
qt_registro_w	integer;


BEGIN 
 
select	count(1) 
into STRICT	qt_registro_w 
from	pls_proposta_plano 
where	nr_seq_proposta	= nr_seq_proposta_p  LIMIT 1;
 
if (qt_registro_w > 0) then 
	select	max(nr_seq_plano), 
		max(nr_seq_tabela) 
	into STRICT	nr_seq_plano_p, 
		nr_seq_tabela_p 
	from	pls_proposta_plano 
	where	nr_seq_proposta	= nr_seq_proposta_p;
end if;
 
select	count(1) 
into STRICT	qt_registro_w 
from	pls_proposta_pagador 
where	nr_seq_proposta	= nr_seq_proposta_p  LIMIT 1;
 
if (qt_registro_w > 0) then 
	select	max(nr_sequencia) 
	into STRICT	nr_sequencia_p 
	from	pls_proposta_pagador 
	where	nr_seq_proposta	= nr_seq_proposta_p;
end if;
 
select	max(nr_seq_motivo_inclusao) 
into STRICT	nr_seq_motivo_inclusao_p 
from	pls_parametros 
where	cd_estabelecimento	= cd_estabelecimento_p;
 
select	count(y.nr_sequencia) 
into STRICT	qt_registro_w 
from	pls_segurado     y, 
	pls_contrato     z 
where	z.nr_sequencia = y.nr_seq_contrato 
and	coalesce(y.nr_seq_titular::text, '') = '' 
and	z.nr_contrato = nr_seq_contrato_p;
 
if (qt_registro_w = 1) and (ie_tipo_proposta_p = 2) and (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then 
	nr_seq_titular_contrato_p := pls_obter_titular_contrato_pf(nr_seq_proposta_p, nr_seq_contrato_p, nm_usuario_p, cd_estabelecimento_p, nr_seq_titular_contrato_p);
	 
	select	dt_contratacao 
	into STRICT 	dt_contratacao_p 
	from 	pls_proposta_beneficiario 
	where 	nr_sequencia = nr_seq_titular_contrato_p;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
