CREATE OR REPLACE PROCEDURE pls_obter_dados_pf_identific ( cd_pessoa_fisica_p text, nm_pessoa_fisica_p INOUT text, dt_nascimento_p INOUT timestamp, nr_cpf_p INOUT text, nm_mae_p INOUT text, cd_nacionalidade_p INOUT text, ie_sexo_p INOUT text, nr_identidade_p INOUT text, nr_telefone_celular_p INOUT text, cd_cep_p INOUT text, ds_endereco_p INOUT text, nr_endereco_p INOUT bigint, ds_complemento_p INOUT text, cd_municipio_ibge_p INOUT text, ds_municipio_ibge_p INOUT text, sg_estado_p INOUT text, nr_ddi_telefone_p INOUT text, nr_ddd_telefone_p INOUT text, nr_telefone_p INOUT text, ds_email_p INOUT text ) AS $body$
BEGIN

select	a.nm_pessoa_fisica,
	a.dt_nascimento,
	a.nr_cpf,
	(	select	x.NM_CONTATO
		from	compl_pessoa_fisica	x
		where	x.cd_pessoa_fisica	= a.cd_pessoa_fisica
		and	x.ie_tipo_complemento	= '5') nm_mae,
	a.cd_nacionalidade,
	a.ie_sexo,
	a.nr_identidade,
	a.nr_telefone_celular
into STRICT	nm_pessoa_fisica_p,
	dt_nascimento_p,
	nr_cpf_p,
	nm_mae_p,
	cd_nacionalidade_p,
	ie_sexo_p,
	nr_identidade_p,
	nr_telefone_celular_p
from	pessoa_fisica		a
where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;

begin
select	b.cd_cep,
	b.ds_endereco,
	b.nr_endereco,
	b.ds_complemento,
	b.cd_municipio_ibge,
	substr(obter_desc_municipio_ibge(b.cd_municipio_ibge),1,255) ds_municipio_ibge,
	b.sg_estado,
	b.nr_ddi_telefone,
	b.nr_ddd_telefone,
	b.nr_telefone,
	b.ds_email
into STRICT	cd_cep_p,
	ds_endereco_p,
	nr_endereco_p,
	ds_complemento_p,
	cd_municipio_ibge_p,
	ds_municipio_ibge_p,
	sg_estado_p,
	nr_ddi_telefone_p,
	nr_ddd_telefone_p,
	nr_telefone_p,
	ds_email_p
from	compl_pessoa_fisica b
where	b.cd_pessoa_fisica	= cd_pessoa_fisica_p
and	b.ie_tipo_complemento	= '1';
exception
when others then
	cd_cep_p		:= null;
	ds_endereco_p		:= null;
	nr_endereco_p		:= null;
	ds_complemento_p	:= null;
	cd_municipio_ibge_p	:= null;
	ds_municipio_ibge_p	:= null;
	sg_estado_p		:= null;
	nr_ddi_telefone_p	:= null;
	nr_ddd_telefone_p	:= null;
	nr_telefone_p		:= null;
	ds_email_p		:= null;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
