CREATE OR REPLACE PROCEDURE pls_obter_dados_prog_reajuste ( nr_seq_lote_prog_p bigint, qt_contratos_p INOUT text, vl_receita_p INOUT text, vl_despesas_p INOUT text, tx_resultado_p INOUT text, tx_sinistralidade_p INOUT text) AS $body$
DECLARE


vl_receita_w	double precision;
vl_despesas_w	double precision;
qt_contratos_w	bigint;
vl_resultado_w	double precision;


BEGIN

select	sum(vl_receita),
	sum(vl_despesas),
	count(1)
into STRICT	vl_receita_w,
	vl_despesas_w,
	qt_contratos_w
from	pls_prog_reaj_coletivo
where	nr_seq_lote	= nr_seq_lote_prog_p;

qt_contratos_p	:= qt_contratos_w;
vl_receita_p	:= campo_mascara_virgula(vl_receita_w);
vl_despesas_p	:= campo_mascara_virgula(vl_despesas_w);


vl_resultado_w	:= vl_receita_w - vl_despesas_w;
begin
tx_resultado_p	:= campo_mascara_virgula_casas((vl_resultado_w * 100) / vl_receita_w,4);
exception
when others then
	tx_resultado_p	:= 0;
end;

begin
tx_sinistralidade_p	:= campo_mascara_virgula_casas((vl_despesas_w / vl_receita_w) * 100,4);
exception
when others then
	tx_sinistralidade_p	:= 0;
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
