CREATE OR REPLACE PROCEDURE pls_obter_dados_repasse ( dt_referencia_p timestamp, nr_seq_segurado_p bigint, nr_seq_congenere_p bigint, ie_tipo_repasse_p INOUT text, ie_tipo_compartilhamento_p INOUT bigint, dt_repasse_p INOUT timestamp, dt_fim_repasse_p INOUT timestamp) AS $body$
DECLARE


/* 	Utilizada nas rotinas de atualização das contas contábeis e registros auxiliares, 
	para obter os dados de compartilhamento de beneficiários				*/
nr_seq_repasse_w		pls_segurado_repasse.nr_sequencia%type;


BEGIN

select 	max(a.nr_sequencia)
into STRICT	nr_seq_repasse_w
from	pls_segurado_repasse a
where	a.nr_seq_segurado	= nr_seq_segurado_p
and	dt_referencia_p between a.dt_repasse and coalesce(a.dt_fim_repasse,dt_referencia_p)
and	coalesce(a.nr_seq_congenere_atend,0) = coalesce(nr_seq_congenere_p,0);

if (nr_seq_repasse_w IS NOT NULL AND nr_seq_repasse_w::text <> '') then
	select 	ie_tipo_compartilhamento,
		ie_tipo_repasse,
		dt_repasse,
		dt_fim_repasse
	into STRICT	ie_tipo_compartilhamento_p,
		ie_tipo_repasse_p,
		dt_repasse_p,
		dt_fim_repasse_p
	from 	pls_segurado_repasse
	where	nr_sequencia = nr_seq_repasse_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
