CREATE OR REPLACE PROCEDURE pls_obter_diaria_autorizada ( nr_seq_guia_p bigint, cd_estabelecimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nm_usuario_p text, qt_dia_autorizado_p INOUT bigint) AS $body$
DECLARE


qt_dia_autorizado_w		bigint	:= 0;
qt_dia_prorrogacao_w		bigint	:= 0;
nr_seq_guia_princ_w		bigint;


BEGIN

if (coalesce(nr_seq_guia_p,0) > 0) then
	begin
		select	coalesce(qt_dia_autorizado,0)
		into STRICT	qt_dia_autorizado_w
		from	pls_guia_plano
		where	nr_sequencia		= nr_seq_guia_p
		and	cd_estabelecimento		= cd_estabelecimento_p;

	exception
	when others then
		qt_dia_autorizado_w := 0;
	end;

	--nr_seq_guia_princ_w	:= pls_obter_guia_aut_principal(nr_seq_guia_p); Essa parte da rotina foi comentada , pois na conta é lançada a guia principal e a guia principal nao tem nenhuma guia referenciada.
	begin
		select	coalesce(sum(qt_dia_autorizado),0)
		into STRICT	qt_dia_prorrogacao_w
		from	pls_guia_plano
		where	nr_seq_guia_principal	= nr_seq_guia_p
		and	cd_estabelecimento	= cd_estabelecimento_p;
	exception
	when others then
		qt_dia_prorrogacao_w := 0;
	end;

	qt_dia_autorizado_w	:= coalesce(qt_dia_autorizado_w,0) + coalesce(qt_dia_prorrogacao_w,0);
end if;

if (qt_dia_autorizado_w = 0) and (coalesce(cd_procedimento_p,0) > 0) and (coalesce(nr_seq_guia_p,0) > 0) then
	begin
		select	sum(qt_autorizada)
		into STRICT	qt_dia_autorizado_w
		from	pls_guia_plano_proc
		where	nr_seq_guia		= nr_seq_guia_p
		and	cd_procedimento		= cd_procedimento_p
		and	ie_origem_proced	= ie_origem_proced_p
		and	qt_autorizada		> 0;
	exception
	when others then
		qt_dia_autorizado_w := 0;
	end;

	begin
		select	sum(a.qt_autorizada)
		into STRICT	qt_dia_prorrogacao_w
		from	PLS_GUIA_PLANO_PROC	a
		where	a.cd_procedimento	= cd_procedimento_p
		and	a.ie_origem_proced	= ie_origem_proced_p
		and	a.qt_autorizada		> 0
		and	a.nr_seq_guia		in (	SELECT 	x.nr_sequencia
							from	pls_guia_plano	x
							where	x.nr_seq_guia_principal	= nr_seq_guia_p
							and	x.cd_estabelecimento	= cd_estabelecimento_p);
	exception
	when others then
		qt_dia_prorrogacao_w := 0;
	end;
	qt_dia_autorizado_w	:= coalesce(qt_dia_autorizado_w,0) + coalesce(qt_dia_prorrogacao_w,0);
end if;
qt_dia_autorizado_p	:= coalesce(qt_dia_autorizado_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obter_diaria_autorizada ( nr_seq_guia_p bigint, cd_estabelecimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nm_usuario_p text, qt_dia_autorizado_p INOUT bigint) FROM PUBLIC;

