CREATE OR REPLACE PROCEDURE pls_obter_ds_indicacao_clinica ( dt_competencia_p text, ie_tipo_p text, nr_seq_protocolo_p bigint, ie_indicacao_clinica_p text, nr_seq_atendimento_p bigint, cd_doenca_p bigint, cd_medico_executor_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ds_indicacao_clinica_p INOUT text) AS $body$
DECLARE



ie_existe_param_w		varchar(4000);
ds_indicacao_clinica_w	varchar(300);
ds_retorno_w		varchar(255);
dt_competencia_w	timestamp;


BEGIN
if (dt_competencia_p IS NOT NULL AND dt_competencia_p::text <> '') and (ie_tipo_p IS NOT NULL AND ie_tipo_p::text <> '') and (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	if (cd_medico_executor_p IS NOT NULL AND cd_medico_executor_p::text <> '') then
		ds_retorno_w := pls_consistir_medico(cd_medico_executor_p, nm_usuario_p, ds_retorno_w);
		if (ds_retorno_w <> '') then
			-- #@DS_RETORNO#@
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266847, 'DS_RETORNO=' || ds_retorno_w);
		end if;
	end if;

	dt_competencia_w := to_date(dt_competencia_p,'dd/mm/yyyy hh24:mi:ss');

	CALL pls_consiste_dt_competencia(
		dt_competencia_w,
		null,
		ie_tipo_p,
		nr_seq_protocolo_p,
		nm_usuario_p,
		cd_estabelecimento_p);

	if (ie_indicacao_clinica_p IS NOT NULL AND ie_indicacao_clinica_p::text <> '') and (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
		begin
		select	pls_obter_se_item_lista(ie_indicacao_clinica_p, lpad(pls_obter_cd_tipo_atend_tiss(nr_seq_atendimento_p, cd_estabelecimento_p),2,0))
		into STRICT	ie_existe_param_w
		;

		if (ie_existe_param_w = 'S') and (cd_doenca_p IS NOT NULL AND cd_doenca_p::text <> '') then
			begin
			select	substr(obter_desc_cid(cd_doenca_p),1,255)
			into STRICT	ds_indicacao_clinica_w
			;
			end;
		end if;
		end;
	end if;

	ds_indicacao_clinica_p := ds_indicacao_clinica_w;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
