CREATE OR REPLACE PROCEDURE pls_obter_estagio_requisicao ( nr_sequencia_p bigint, qt_registro_p INOUT bigint, ie_estagio_p INOUT bigint) AS $body$
DECLARE


qt_registro_w	bigint;
ie_estagio_w	smallint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	ptu_resposta_autorizacao
	where	nr_seq_requisicao = nr_sequencia_p;

	select	coalesce(ie_estagio, 0)
	into STRICT	ie_estagio_w
	from	pls_requisicao
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;
commit;

qt_registro_p	:= qt_registro_w;
ie_estagio_p	:= ie_estagio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
