SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_obter_evento_item (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_material_p bigint, cd_estabelecimento_p bigint, ie_tipo_guia_p text, ie_origem_conta_p text, nr_seq_tipo_atend_p text, ie_tipo_despesa_p text, nr_seq_segurado_p bigint, nr_seq_conta_p bigint, ie_taxa_coleta_p text, nr_seq_prest_pgto_p bigint, nr_seq_evento_p INOUT bigint, nr_seq_regra_p INOUT bigint, ie_origem_p text, ie_regime_atendimento_p pls_conta.ie_regime_atendimento%type default null, ie_saude_ocupacional_p pls_conta.ie_saude_ocupacional%type default null) AS $body$
DECLARE

/*
ie_origem_p	
		'C' - Conta Mdica
		'G' - Recurso de Glosa
		
nr_seq_prest_pgto_p
	- Utilizado para pls_conta_mat
*/
nr_seq_evento_w			bigint;
nr_seq_regra_w			bigint;
nr_seq_regra_ww			bigint;
ie_origem_proced_w		bigint;
cd_grupo_proc_w			bigint;
cd_especialidade_w		bigint;
cd_area_procedimento_w		bigint;
ie_tipo_despesa_proc_w		varchar(10);
ie_tipo_despesa_mat_w		varchar(10);
nr_seq_grupo_servico_w		bigint;
ie_grupo_servico_w		varchar(1) := 'S';
ie_preco_w			varchar(1);
ie_tipo_segurado_w		varchar(3);
nr_seq_plano_w			bigint;
nr_seq_estrut_regra_w		bigint;
ie_estrut_mat_w			varchar(1);
nr_seq_evento_ww		bigint;
nr_seq_prestador_w		bigint;
nr_seq_protocolo_w		bigint;
ie_apresentacao_prot_w		varchar(3);
cd_guia_w			pls_analise_conta.cd_guia%type;
nr_seq_analise_w		pls_analise_conta.nr_sequencia%type;
nr_seq_segurado_w		pls_analise_conta.nr_seq_segurado%type;
nr_seq_prestador_princ_w	pls_analise_conta.nr_seq_prestador%type;
nr_seq_conta_princ_w		pls_conta.nr_sequencia%type;
ie_tipo_guia_princ_w		pls_conta.ie_tipo_guia%type;
nr_seq_tipo_atend_princ_w	pls_conta.nr_seq_tipo_atendimento%type;
nr_seq_tipo_prest_prot_w	pls_prestador.nr_seq_tipo_prestador%type;
nr_seq_tipo_prestador_w		pls_prestador.nr_seq_tipo_prestador%type;
qt_tipo_prest_w			integer;
nr_seq_prestador_prot_w		pls_evento_regra_producao.nr_seq_prestador_prot%type;
ie_origem_w			varchar(2);
ie_regime_atendimento_princ_w	pls_conta.ie_regime_atendimento%type;

C01 CURSOR FOR
	SELECT	b.nr_sequencia nr_seq_evento,
		a.nr_sequencia nr_seq_regra,
		a.nr_seq_grupo_servico,
		a.nr_seq_estrut_mat
	from	pls_evento			b,
		pls_evento_regra_producao	a
	where	a.nr_seq_evento			= b.nr_sequencia
	and	b.cd_estabelecimento		= cd_estabelecimento_p
	and	a.ie_situacao			= 'A'
	and	b.ie_situacao			= 'A'
	and	((coalesce(a.cd_procedimento::text, '') = '') or (a.cd_procedimento	= cd_procedimento_p AND ie_origem_proced = ie_origem_proced_p))
	and	((coalesce(a.cd_grupo_proc::text, '') = '') or (a.cd_grupo_proc		= cd_grupo_proc_w))
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade 	= cd_especialidade_w))
	and	((coalesce(a.cd_area_procedimento::text, '') = '') or (a.cd_area_procedimento	= cd_area_procedimento_w))
	and	((coalesce(a.nr_seq_material::text, '') = '') or (a.nr_seq_material		= nr_seq_material_p))
	and	((coalesce(a.ie_tipo_guia::text, '') = '') or (a.ie_tipo_guia 		= coalesce(ie_tipo_guia_p,'X')))
	and	((coalesce(a.ie_origem_conta::text, '') = '') or (a.ie_origem_conta 		= coalesce(ie_origem_conta_p,'M')))
	and	((coalesce(a.nr_seq_tipo_atendimento::text, '') = '') or (a.nr_seq_tipo_atendimento 	= nr_seq_tipo_atend_p))
	and	((coalesce(a.ie_regime_atendimento::text, '') = '') or (a.ie_regime_atendimento 	= ie_regime_atendimento_p))
	and	((coalesce(a.ie_saude_ocupacional::text, '') = '') or (a.ie_saude_ocupacional 	= ie_saude_ocupacional_p))
	and	((coalesce(a.ie_tipo_despesa_proc::text, '') = '') or (a.ie_tipo_despesa_proc 	= ie_tipo_despesa_proc_w))
	and	((coalesce(a.ie_tipo_despesa_mat::text, '') = '') or (a.ie_tipo_despesa_mat 	= ie_tipo_despesa_mat_w))
	and	((coalesce(a.ie_preco::text, '') = '') or (a.ie_preco 		= ie_preco_w))
	and	((coalesce(a.ie_tipo_segurado::text, '') = '') or (a.ie_tipo_segurado 	= ie_tipo_segurado_w))
	and	((coalesce(a.ie_taxa_coleta::text, '') = '') or (a.ie_taxa_coleta		= 'N') or (ie_taxa_coleta_p = 'S'))
	and	((coalesce(a.ie_apresentacao_prot::text, '') = '') or (a.ie_apresentacao_prot 	= 'T') or (a.ie_apresentacao_prot = ie_apresentacao_prot_w))
	and	((coalesce(a.nr_seq_tipo_atend_princ::text, '') = '') or (a.nr_seq_tipo_atend_princ	= nr_seq_tipo_atend_princ_w))
	and	((coalesce(a.ie_regime_atend_princ::text, '') = '') or (a.ie_regime_atend_princ	= ie_regime_atendimento_princ_w))
	and	((coalesce(a.ie_tipo_guia_princ::text, '') = '') or (a.ie_tipo_guia_princ	= ie_tipo_guia_princ_w))
	and	((coalesce(a.nr_seq_prestador_prot::text, '') = '') or (a.nr_seq_prestador_prot	= nr_seq_prestador_prot_w))
	and	((exists (SELECT	1 
				from	pls_prestador_tipo	y,
					pls_protocolo_conta 	x
				where	x.nr_sequencia		= nr_seq_protocolo_w
				and	y.nr_seq_prestador 	= x.nr_seq_prestador
				and	y.nr_seq_tipo		= a.nr_seq_tipo_prest_prot
				and	x.dt_mes_competencia between y.dt_inicio_vigencia_ref and y.dt_fim_vigencia_ref)) 
		or (a.nr_seq_tipo_prest_prot = nr_seq_tipo_prest_prot_w)
		or (coalesce(a.nr_seq_tipo_prest_prot::text, '') = ''))
	and	((exists (select 1
				from	pls_prestador_tipo	x
				where	x.nr_seq_prestador	= nr_seq_prestador_w
				and	x.nr_seq_tipo		= a.nr_seq_tipo_prestador
				and	clock_timestamp() between x.dt_inicio_vigencia_ref and x.dt_fim_vigencia_ref)) 
		or (a.nr_seq_tipo_prestador = nr_seq_tipo_prestador_w)
		or (coalesce(a.nr_seq_tipo_prestador::text, '') = ''))
	and	b.ie_tipo_evento 		= ie_origem_w
	order by
		coalesce(a.ie_apresentacao_prot,'T'),
		coalesce(a.ie_taxa_coleta,'N'),
		coalesce(a.cd_procedimento,0),
		coalesce(a.cd_grupo_proc,0),
		coalesce(a.cd_especialidade,0),
		coalesce(a.cd_area_procedimento,0),
		coalesce(a.nr_seq_material,0),
		coalesce(a.nr_seq_estrut_mat,0),
		coalesce(a.nr_seq_tipo_atend_princ,0),
		coalesce(a.ie_tipo_guia_princ, '0'),
		coalesce(a.ie_tipo_segurado,'X'),
		coalesce(a.nr_seq_prestador_prot,0),
		coalesce(a.nr_seq_tipo_prest_prot,0),
		coalesce(a.nr_seq_tipo_prestador,0);
		
C02 CURSOR FOR
	SELECT	b.nr_sequencia nr_seq_evento,
		a.nr_sequencia nr_seq_regra,
		a.nr_seq_grupo_servico,
		a.nr_seq_estrut_mat
	from	pls_evento			b,
		pls_evento_regra_producao	a
	where	a.nr_seq_evento			= b.nr_sequencia
	and	b.cd_estabelecimento		= cd_estabelecimento_p
	and	a.ie_situacao			= 'A'
	and	b.ie_situacao			= 'A'
	and	((coalesce(a.cd_procedimento::text, '') = '') or (a.cd_procedimento	= cd_procedimento_p AND ie_origem_proced = ie_origem_proced_p))
	and	((coalesce(a.cd_grupo_proc::text, '') = '') or (a.cd_grupo_proc		= cd_grupo_proc_w))
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade 	= cd_especialidade_w))
	and	((coalesce(a.cd_area_procedimento::text, '') = '') or (a.cd_area_procedimento	= cd_area_procedimento_w))
	and	((coalesce(a.nr_seq_material::text, '') = '') or (a.nr_seq_material		= nr_seq_material_p))
	and	((coalesce(a.ie_tipo_guia::text, '') = '') or (a.ie_tipo_guia 		= coalesce(ie_tipo_guia_p,'X')))
	and	((coalesce(a.ie_origem_conta::text, '') = '') or (a.ie_origem_conta 		= coalesce(ie_origem_conta_p,'M')))
	and	((coalesce(a.nr_seq_tipo_atendimento::text, '') = '') or (a.nr_seq_tipo_atendimento 	= nr_seq_tipo_atend_p))
	and	((coalesce(a.ie_regime_atendimento::text, '') = '') or (a.ie_regime_atendimento 	= ie_regime_atendimento_p))
	and	((coalesce(a.ie_saude_ocupacional::text, '') = '') or (a.ie_saude_ocupacional 	= ie_saude_ocupacional_p))
	and	((coalesce(a.ie_tipo_despesa_proc::text, '') = '') or (a.ie_tipo_despesa_proc 	= ie_tipo_despesa_proc_w))
	and	((coalesce(a.ie_tipo_despesa_mat::text, '') = '') or (a.ie_tipo_despesa_mat 	= ie_tipo_despesa_mat_w))
	and	((coalesce(a.ie_preco::text, '') = '') or (a.ie_preco 		= ie_preco_w))
	and	((coalesce(a.ie_tipo_segurado::text, '') = '') or (a.ie_tipo_segurado 	= ie_tipo_segurado_w))
	and	((coalesce(a.ie_taxa_coleta::text, '') = '') or (a.ie_taxa_coleta		= 'N') or (ie_taxa_coleta_p = 'S'))
	and	((coalesce(a.ie_apresentacao_prot::text, '') = '') or (a.ie_apresentacao_prot 	= 'T') or (a.ie_apresentacao_prot = ie_apresentacao_prot_w))
	and	((coalesce(a.nr_seq_tipo_atend_princ::text, '') = '') or (a.nr_seq_tipo_atend_princ	= nr_seq_tipo_atend_princ_w))
	and	((coalesce(a.ie_regime_atend_princ::text, '') = '') or (a.ie_regime_atend_princ	= ie_regime_atendimento_princ_w))
	and	((coalesce(a.ie_tipo_guia_princ::text, '') = '') or (a.ie_tipo_guia_princ	= ie_tipo_guia_princ_w))
	and	((coalesce(a.nr_seq_prestador_prot::text, '') = '') or (a.nr_seq_prestador_prot	= nr_seq_prestador_prot_w))
	and	((coalesce(a.nr_seq_tipo_prest_prot::text, '') = '') or (a.nr_seq_tipo_prest_prot 	= nr_seq_tipo_prest_prot_w))
	and	((coalesce(a.nr_seq_tipo_prestador::text, '') = '') or (a.nr_seq_tipo_prestador 	= nr_seq_tipo_prestador_w))
	and	b.ie_tipo_evento 		= ie_origem_w
	order by
		coalesce(a.ie_apresentacao_prot,'T'),
		coalesce(a.ie_taxa_coleta,'N'),
		coalesce(a.cd_procedimento,0),
		coalesce(a.cd_grupo_proc,0),
		coalesce(a.cd_especialidade,0),
		coalesce(a.cd_area_procedimento,0),
		coalesce(a.nr_seq_material,0),
		coalesce(a.nr_seq_estrut_mat,0),
		coalesce(a.nr_seq_tipo_atend_princ,0),
		coalesce(a.ie_tipo_guia_princ, '0'),
		coalesce(a.ie_tipo_segurado,'X'),
		coalesce(a.nr_seq_prestador_prot,0),
		coalesce(a.nr_seq_tipo_prest_prot,0),
		coalesce(a.nr_seq_tipo_prestador,0);

BEGIN

if (ie_origem_p = 'C') then
	ie_origem_w := 'P';
else
	ie_origem_w := ie_origem_p;
end if;

SELECT * FROM pls_obter_estrut_proc(	cd_procedimento_p, ie_origem_proced_p, cd_area_procedimento_w, cd_especialidade_w, cd_grupo_proc_w, ie_origem_proced_w) INTO STRICT cd_area_procedimento_w, cd_especialidade_w, cd_grupo_proc_w, ie_origem_proced_w;
	
select	max(coalesce(a.cd_guia, b.cd_guia)),
	max(b.nr_seq_analise),
	max(coalesce(a.nr_seq_segurado, b.nr_seq_segurado)),
	max(coalesce(a.nr_seq_prestador, b.nr_seq_prestador)),
	max(b.nr_seq_conta_princ)
into STRICT	cd_guia_w,
	nr_seq_analise_w,
	nr_seq_segurado_w,
	nr_seq_prestador_princ_w,
	nr_seq_conta_princ_w
FROM pls_conta b
LEFT OUTER JOIN pls_analise_conta a ON (b.nr_seq_analise = a.nr_sequencia)
WHERE b.nr_sequencia		= nr_seq_conta_p;

select 	count(1)
into STRICT	qt_tipo_prest_w
from 	pls_prestador_tipo LIMIT 1;

select	max(ie_tipo_guia),
	max(nr_seq_tipo_atendimento),
	max(ie_regime_atendimento)
into STRICT	ie_tipo_guia_princ_w,
	nr_seq_tipo_atend_princ_w,
	ie_regime_atendimento_princ_w
from	pls_conta
where	nr_sequencia = nr_seq_conta_princ_w;

select 	max(ie_tipo_segurado),
	max(nr_seq_plano)
into STRICT	ie_tipo_segurado_w,
	nr_seq_plano_w
from 	pls_segurado
where	nr_sequencia = nr_seq_segurado_p;

select	max(ie_preco)
into STRICT	ie_preco_w
from	pls_plano
where	nr_sequencia = nr_seq_plano_w;

select	a.nr_seq_prestador_exec,
	a.nr_seq_protocolo,
	CASE WHEN coalesce(a.ie_tipo_segurado::text, '') = '' THEN  ie_tipo_segurado_w  ELSE ie_tipo_segurado END
into STRICT	nr_seq_prestador_w,
	nr_seq_protocolo_w,
	ie_tipo_segurado_w
from	pls_conta a
where	a.nr_sequencia = nr_seq_conta_p;

nr_seq_prestador_w := coalesce(nr_seq_prest_pgto_p,nr_seq_prestador_w);

select	max(nr_seq_tipo_prestador)
into STRICT	nr_seq_tipo_prestador_w
from	pls_prestador
where	nr_sequencia = nr_seq_prestador_w;

select	a.ie_apresentacao,
	b.nr_seq_tipo_prestador,
	a.nr_seq_prestador
into STRICT	ie_apresentacao_prot_w,
	nr_seq_tipo_prest_prot_w,
	nr_seq_prestador_prot_w
FROM pls_protocolo_conta a
LEFT OUTER JOIN pls_prestador b ON (a.nr_seq_prestador = b.nr_sequencia)
WHERE a.nr_sequencia 		= nr_seq_protocolo_w;

if (coalesce(cd_procedimento_p,0) > 0) then
	ie_tipo_despesa_proc_w	:= ie_tipo_despesa_p;
elsif (coalesce(nr_seq_material_p,0) > 0) then
	ie_tipo_despesa_mat_w	:= ie_tipo_despesa_p;	
end if;

if (qt_tipo_prest_w	> 0) then
	open C01;
	loop
	fetch C01 into	
		nr_seq_evento_ww,
		nr_seq_regra_ww,
		nr_seq_grupo_servico_w,
		nr_seq_estrut_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_grupo_servico_w	:= 'S';
		ie_estrut_mat_w		:= 'S';
		
		if (nr_seq_estrut_regra_w IS NOT NULL AND nr_seq_estrut_regra_w::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
			ie_estrut_mat_w	:= pls_obter_se_mat_estrutura(nr_seq_material_p, nr_seq_estrut_regra_w);
		elsif (nr_seq_estrut_regra_w IS NOT NULL AND nr_seq_estrut_regra_w::text <> '') then
			ie_estrut_mat_w := 'N';
		end if;
		
		if (ie_estrut_mat_w = 'S') then
			/* Grupo de servios */

			if (coalesce(nr_seq_grupo_servico_w,0) > 0) then
				ie_grupo_servico_w := pls_se_grupo_preco_servico(nr_seq_grupo_servico_w, cd_procedimento_p, ie_origem_proced_w);
			end if;	
			
			if (ie_grupo_servico_w	= 'S') then
				nr_seq_regra_w	:= nr_seq_regra_ww;
				nr_seq_evento_w	:= nr_seq_evento_ww;
			end if;
		end if;
		end;
	end loop;
	close C01;
else
	for r_c02_w in C02() loop
		begin
		nr_seq_evento_ww	:= r_c02_w.nr_seq_evento;
		nr_seq_regra_ww		:= r_c02_w.nr_seq_regra;
		nr_seq_grupo_servico_w	:= r_c02_w.nr_seq_grupo_servico;
		nr_seq_estrut_regra_w	:= r_c02_w.nr_seq_estrut_mat;
		
		ie_grupo_servico_w	:= 'S';
		ie_estrut_mat_w		:= 'S';
		
		if (nr_seq_estrut_regra_w IS NOT NULL AND nr_seq_estrut_regra_w::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
			ie_estrut_mat_w	:= pls_obter_se_mat_estrutura(nr_seq_material_p, nr_seq_estrut_regra_w);
		elsif (nr_seq_estrut_regra_w IS NOT NULL AND nr_seq_estrut_regra_w::text <> '') then
			ie_estrut_mat_w := 'N';
		end if;
		
		if (ie_estrut_mat_w = 'S') then
			/* Grupo de servios */

			if (coalesce(nr_seq_grupo_servico_w,0) > 0) then
				ie_grupo_servico_w	:= pls_se_grupo_preco_servico(nr_seq_grupo_servico_w, cd_procedimento_p, ie_origem_proced_w);
			end if;	
			
			if (ie_grupo_servico_w	= 'S') then
				nr_seq_regra_w	:= nr_seq_regra_ww;
				nr_seq_evento_w	:= nr_seq_evento_ww;
			end if;
		end if;
		
		end;
	end loop;
end if;

nr_seq_evento_p	:= nr_seq_evento_w;
nr_seq_regra_p	:= nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obter_evento_item (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_material_p bigint, cd_estabelecimento_p bigint, ie_tipo_guia_p text, ie_origem_conta_p text, nr_seq_tipo_atend_p text, ie_tipo_despesa_p text, nr_seq_segurado_p bigint, nr_seq_conta_p bigint, ie_taxa_coleta_p text, nr_seq_prest_pgto_p bigint, nr_seq_evento_p INOUT bigint, nr_seq_regra_p INOUT bigint, ie_origem_p text, ie_regime_atendimento_p pls_conta.ie_regime_atendimento%type default null, ie_saude_ocupacional_p pls_conta.ie_saude_ocupacional%type default null) FROM PUBLIC;

