CREATE OR REPLACE PROCEDURE pls_obter_excecao_autogerado ( nr_seq_regra_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_regra_excecao_p INOUT bigint) AS $body$
DECLARE

				 
nr_seq_regra_w			bigint;
nr_seq_regra_ww			bigint;
nr_seq_grupo_servico_w		bigint;
ie_grupo_servico_w		varchar(10)	:= 'S';
				
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.nr_seq_grupo_servico 
	from	pls_excecao_autogerado	a 
	where	a.ie_situacao		= 'A' 
	and	a.nr_seq_regra		= nr_seq_regra_p 
	and	coalesce(a.cd_procedimento, coalesce(cd_procedimento_p,0))	= coalesce(cd_procedimento_p,0) 
	and	coalesce(a.ie_origem_proced, coalesce(ie_origem_proced_p,0))	= coalesce(ie_origem_proced_p,0) 
	order by coalesce(a.cd_procedimento,0);


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_regra_ww, 
	nr_seq_grupo_servico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ie_grupo_servico_w		:= 'S';
	 
	/* Grupo de serviÃ§os */
 
	if (coalesce(nr_seq_grupo_servico_w,0) > 0) then 
		ie_grupo_servico_w	:= pls_se_grupo_preco_servico(nr_seq_grupo_servico_w, cd_procedimento_p, ie_origem_proced_p);
	end if;	
	 
	if (ie_grupo_servico_w	= 'S') then 
		nr_seq_regra_w	:= nr_seq_regra_ww;
	end if;
	end;
end loop;
close C01;
 
nr_seq_regra_excecao_p	:= coalesce(nr_seq_regra_w,0);
 
end;
$body$
