CREATE OR REPLACE PROCEDURE pls_obter_excecao_lanc_aut ( nr_seq_regra_p bigint, ie_origem_conta_p text, ie_tipo_segurado_p pls_segurado.ie_tipo_segurado%type, nr_seq_congenere_p pls_congenere.nr_sequencia%type, ie_excecao_p INOUT text) AS $body$
DECLARE


qt_excecao_w			integer;


BEGIN
select	count(1)
into STRICT	qt_excecao_w
from	pls_regra_lanc_aut_excecao a
where	a.nr_seq_regra		= nr_seq_regra_p
and	((a.ie_origem_conta	= ie_origem_conta_p) or (coalesce(a.ie_origem_conta::text, '') = ''))
and	((a.ie_tipo_segurado	= ie_tipo_segurado_p) or (coalesce(a.ie_tipo_segurado::text, '') = ''))
and	((coalesce(a.nr_seq_grupo_operadora::text, '') = '') or (exists (	SELECT	1
							from	pls_preco_operadora	x
							where	x.nr_seq_congenere	= nr_seq_congenere_p
							and	x.nr_seq_grupo		= a.nr_seq_grupo_operadora)));

ie_excecao_p	:= 'N';
if (qt_excecao_w > 0) then
	ie_excecao_p	:= 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
