CREATE OR REPLACE PROCEDURE pls_obter_exce_tipo_valor_pos ( nr_seq_regra_p pls_regra_preco_pos_estab.nr_sequencia%type, dados_conta_pos_p pls_cta_valorizacao_pck.dados_conta_pos, ie_excecao_p INOUT text) AS $body$
DECLARE

						
ie_excecao_w		varchar(2) := 'N';
nr_seq_congenere_w	pls_segurado.nr_seq_congenere%type;
nr_seq_contrato_w	pls_segurado.nr_seq_contrato%type;
nr_seq_intercambio_w	pls_segurado.nr_seq_intercambio%type;

C01 CURSOR(	nr_seq_regra_pc		pls_regra_preco_pos_estab.nr_sequencia%type,
		nr_seq_contrato_pc	pls_contrato.nr_sequencia%type,
		nr_seq_intercambio_pc	pls_intercambio.nr_sequencia%type,
		cd_procedimento_pc	procedimento.cd_procedimento%type,
		ie_origem_proced_pc	procedimento.ie_classificacao%type,
		nr_seq_congenere_pc	pls_congenere.nr_sequencia%type,
		dt_referencia_pc	pls_conta.dt_atendimento_referencia%type,
		ie_origem_protocolo_pc	pls_protocolo_conta.ie_origem_protocolo%type,
		ie_tipo_protocolo_pc	pls_protocolo_conta.ie_tipo_protocolo%type) FOR
	SELECT	nr_seq_grupo_operadora,
		nr_seq_grupo_servico
	from	pls_excecao_preco_pos	z
	where	z.nr_seq_regra 			= nr_seq_regra_pc
	and	((coalesce(z.nr_seq_contrato::text, '') = '') or (z.nr_seq_contrato		= nr_seq_contrato_pc))	
	and	((coalesce(z.nr_seq_intercambio::text, '') = '') or (z.nr_seq_intercambio 	= nr_seq_intercambio_pc))
	and	((coalesce(z.cd_procedimento::text, '') = '') or (z.cd_procedimento = cd_procedimento_pc AND z.ie_origem_proced = ie_origem_proced_pc))
	and	((coalesce(z.nr_seq_congenere::text, '') = '') or (z.nr_seq_congenere		= nr_seq_congenere_pc))
	and	((coalesce(z.nr_seq_prest_inter::text, '') = '') or (z.nr_seq_prest_inter = dados_conta_pos_p.nr_seq_prest_inter))
	and 	((coalesce(z.nr_seq_grupo_prest_int::text, '') = '') or (pls_obter_se_grupo_prest_int(z.nr_seq_grupo_prest_int, dados_conta_pos_p.nr_seq_prest_inter) = 'S'))
	and		dt_referencia_pc	 	between z.dt_inicio_vigencia and coalesce(z.dt_fim_vigencia,dt_referencia_pc)
	and 	((coalesce(z.ie_tipo_protocolo::text, '') = '') or (z.ie_tipo_protocolo = ie_tipo_protocolo_pc))
	and 	((coalesce(z.ie_origem_protocolo::text, '') = '') or (z.ie_origem_protocolo = ie_origem_protocolo_pc));
						
BEGIN
select	nr_seq_congenere,
	nr_seq_contrato,
	nr_seq_intercambio
into STRICT	nr_seq_congenere_w,
	nr_seq_contrato_w,
	nr_seq_intercambio_w
from	pls_segurado
where	nr_sequencia = dados_conta_pos_p.nr_seq_segurado;

if (dados_conta_pos_p.nr_seq_congenere IS NOT NULL AND dados_conta_pos_p.nr_seq_congenere::text <> '') then
	nr_seq_congenere_w := dados_conta_pos_p.nr_seq_congenere;
end if;

for r_C01_w in C01(	nr_seq_regra_p, nr_seq_contrato_w, nr_seq_intercambio_w, dados_conta_pos_p.cd_procedimento, dados_conta_pos_p.ie_origem_proced,
						dados_conta_pos_p.nr_seq_congenere, dados_conta_pos_p.dt_referencia, dados_conta_pos_p.ie_origem_protocolo, 
						dados_conta_pos_p.ie_tipo_protocolo) loop
	
	ie_excecao_w := 'S';
	
	if (r_C01_w.nr_seq_grupo_operadora IS NOT NULL AND r_C01_w.nr_seq_grupo_operadora::text <> '') then
		
		ie_excecao_w := pls_se_grupo_preco_operadora(r_C01_w.nr_seq_grupo_operadora, nr_seq_congenere_w);
	end if;
	
	if (r_c01_w.nr_seq_grupo_servico IS NOT NULL AND r_c01_w.nr_seq_grupo_servico::text <> '') then
	
		ie_excecao_w := pls_se_grupo_preco_servico( r_c01_w.nr_seq_grupo_servico, dados_conta_pos_p.cd_procedimento, dados_conta_pos_p.ie_origem_proced);

	end if;
	
	if (ie_excecao_w = 'S') then
		exit;
	end if;
end loop;

ie_excecao_p := ie_excecao_w;

end;
$body$
