CREATE OR REPLACE PROCEDURE pls_obter_faixa_etaria (nr_seq_tabela_p bigint, qt_idade_p bigint, nr_seq_plano_preco_p INOUT bigint, vl_preco_p INOUT bigint) AS $body$
DECLARE


qt_idade_w			bigint;
nr_seq_plano_preco_w		bigint;
nr_seq_plano_preco_atual_w	bigint;
qt_idade_inicial_w		bigint;
vl_preco_w			double precision;
vl_preco_atual_w		double precision;

c02 CURSOR FOR 		-- select das faixas etarias
SELECT	a.nr_sequencia,
	a.qt_idade_inicial,
	a.vl_preco_atual
from	pls_plano_preco a
where	a.nr_seq_tabela		= nr_seq_tabela_p
order 	by a.qt_idade_inicial desc;


BEGIN

qt_idade_w		:= qt_idade_p;
if (qt_idade_w < 0) then
	qt_idade_w	:= 0;
end if;

-- Verificar em qual faixa etÃ¡ria o segurado se encaixa
nr_seq_plano_preco_atual_w			:= 0;
vl_preco_atual_w				:= 0;

open c02;
loop
fetch c02 into
	nr_seq_plano_preco_w,
	qt_idade_inicial_w,
	vl_preco_w;
EXIT WHEN NOT FOUND or coalesce(nr_seq_plano_preco_atual_w,0) > 0;  /* apply on c02 */
	if (qt_idade_w >= qt_idade_inicial_w) then
		nr_seq_plano_preco_atual_w	:= nr_seq_plano_preco_w;
		vl_preco_atual_w		:= vl_preco_w;
	end if;
end loop;
close c02;

nr_seq_plano_preco_p	:= nr_seq_plano_preco_atual_w;
vl_preco_p		:= vl_preco_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
