SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_obter_guia_tiss ( nr_seq_conta_p bigint, ie_tipo_guia_tiss_p INOUT text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Regra para verifcar qual o tipo da guia, que a conta deve ter quando importado o A500
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/qt_registro_w			bigint;
ie_tipo_guia_tiss_w		varchar(2);
ie_tipo_guia_tiss_reg_w		varchar(2);
nr_seq_regra_w			bigint;

ie_origem_proced_w		bigint;
cd_procedimento_w		bigint;
cd_area_procedimento_w		bigint;
cd_especialidade_w		bigint;
cd_grupo_proc_w			bigint;
ie_tipo_despesa_proc_tiss_w	varchar(2);
ie_nota_hospitalar_w		varchar(1) := 'N';

C01 CURSOR FOR
	SELECT	ie_tiss_tipo_guia,
		nr_sequencia
	from	pls_regra_guia_tiss
	order by ie_prioridade;

C02 CURSOR FOR
	SELECT	ie_origem_proced,
		cd_procedimento,
		cd_area_procedimento,
		cd_especialidade,
		cd_grupo_proc,
		ie_tipo_despesa_proc_tiss,
		ie_nota_hospitalar
	from	pls_regra_guia_tiss_proc
	where	nr_seq_regra = nr_seq_regra_w;


BEGIN
ie_tipo_guia_tiss_p := null;

select	count(1)
into STRICT	qt_registro_w
from	pls_conta_proc
where	nr_seq_conta = nr_seq_conta_p  LIMIT 1;

if (qt_registro_w > 0) then
	open C01;
	loop
	fetch C01 into
		ie_tipo_guia_tiss_w,
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	count(1)
		into STRICT	qt_registro_w
		from	pls_regra_guia_tiss_proc
		where	nr_seq_regra = nr_seq_regra_w  LIMIT 1;

		if (qt_registro_w = 0) then
			ie_tipo_guia_tiss_reg_w := ie_tipo_guia_tiss_w;
		end if;

		open C02;
		loop
		fetch C02 into
			ie_origem_proced_w,
			cd_procedimento_w,
			cd_area_procedimento_w,
			cd_especialidade_w,
			cd_grupo_proc_w,
			ie_tipo_despesa_proc_tiss_w,
			ie_nota_hospitalar_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			select	count(1)
			into STRICT	qt_registro_w
			from	estrutura_procedimento_v	b,
				pls_conta_proc			a
			where	a.cd_procedimento	= b.cd_procedimento
			and	a.ie_origem_proced	= b.ie_origem_proced
			and	a.nr_seq_conta		= nr_seq_conta_p
			and (a.cd_procedimento 	= cd_procedimento_w or coalesce(cd_procedimento_w::text, '') = '')
			and (a.ie_origem_proced 	= ie_origem_proced_w or coalesce(ie_origem_proced_w::text, '') = '')
			and (b.cd_area_procedimento	= cd_area_procedimento_w or coalesce(cd_area_procedimento_w::text, '') = '')
			and (b.cd_especialidade 	= cd_especialidade_w or coalesce(cd_especialidade_w::text, '') = '')
			and (b.cd_grupo_proc 	= cd_grupo_proc_w or coalesce(cd_grupo_proc_w::text, '') = '')
			and (b.ie_tipo_despesa_tiss	= ie_tipo_despesa_proc_tiss_w or coalesce(ie_tipo_despesa_proc_tiss_w::text, '') = '')  LIMIT 1;

			if (coalesce(ie_nota_hospitalar_w,'N') = 'S') then
				select	count(1)
				into STRICT	qt_registro_w
				from	pls_conta		x,
					ptu_nota_cobranca	w,
					ptu_nota_hospitalar	z
				where	w.nr_sequencia	= x.nr_seq_nota_cobranca
				and	w.nr_sequencia	= z.nr_seq_nota_cobr
				and	x.nr_sequencia	= nr_seq_conta_p  LIMIT 1;
			end if;

			if (qt_registro_w > 0) then
				ie_tipo_guia_tiss_p := ie_tipo_guia_tiss_w;
			end if;
			end;
		end loop;
		close C02;
		end;
	end loop;
	close C01;

	if (ie_tipo_guia_tiss_reg_w IS NOT NULL AND ie_tipo_guia_tiss_reg_w::text <> '') and (coalesce(ie_tipo_guia_tiss_p::text, '') = '') then
		ie_tipo_guia_tiss_p := ie_tipo_guia_tiss_reg_w;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
