CREATE OR REPLACE PROCEDURE pls_obter_incind_mov_benef_ted ( nr_seq_regra_ted_p bigint, cd_municipio_ibge_p text, ie_regulamentacao_p pls_plano.ie_regulamentacao%type, ie_segmentacao_p pls_plano.ie_segmentacao%type, nr_seq_plano_p pls_plano.nr_sequencia%type, nr_seq_contrato_p pls_segurado.nr_seq_contrato%type, ie_tipo_segurado_p pls_segurado.ie_tipo_segurado%type, ie_pcmso_p pls_segurado.ie_pcmso%type, nr_seq_grupo_contratual_p pls_grupo_contrato.nr_sequencia%type, cd_pf_estipulante_p pls_contrato.cd_pf_estipulante%type, ie_incide_lote_p INOUT text) AS $body$
DECLARE


ie_incide_lote_w		varchar(10);
ie_tipo_estipulante_w		pls_ted_regra_mov_incid.ie_tipo_estipulante%type;
C01 CURSOR FOR
	/*Feito cursor para buscar a regra pela mais restritiva drquadros - 647711*/

	SELECT	ie_incide_lote
	from	pls_ted_regra_mov_incid
	where	nr_seq_regra_ted	= nr_seq_regra_ted_p
	and	((cd_municipio_ibge	= cd_municipio_ibge_p) or (coalesce(cd_municipio_ibge::text, '') = ''))
	and	((ie_regulamentacao	= ie_regulamentacao_p) or (coalesce(ie_regulamentacao::text, '') = ''))
	and 	((ie_segmentacao 	= ie_segmentacao_p) or (coalesce(ie_segmentacao::text, '') = ''))
	and 	((nr_seq_plano		= nr_seq_plano_p) or (coalesce(nr_seq_plano::text, '') = ''))
	and 	((nr_seq_contrato	= nr_seq_contrato_p) or (coalesce(nr_seq_contrato::text, '') = ''))
	and	((nr_seq_grupo_contrato = nr_seq_grupo_contratual_p) or (coalesce(nr_seq_grupo_contrato::text, '') = ''))
	and	((ie_tipo_estipulante = ie_tipo_estipulante_w) or (coalesce(ie_tipo_estipulante,'A') = 'A'))
	and	((ie_pcmso = ie_pcmso_p) or (coalesce(ie_pcmso::text, '') = ''))
	and	((ie_tipo_segurado = ie_tipo_segurado_p) or (coalesce(ie_tipo_segurado::text, '') = ''))
	order by	coalesce(cd_municipio_ibge,' '),
			coalesce(ie_regulamentacao,' '),
			coalesce(ie_segmentacao, ' ');

BEGIN

if (cd_pf_estipulante_p IS NOT NULL AND cd_pf_estipulante_p::text <> '') then
	ie_tipo_estipulante_w := 'PF';
else
	ie_tipo_estipulante_w := 'PJ';

end if;

for	r_c01 in C01 loop
	ie_incide_lote_w	:= r_c01.ie_incide_lote;
end loop;

if (coalesce(ie_incide_lote_w::text, '') = '') then
	ie_incide_lote_w	:= 'N';
end if;

ie_incide_lote_p	:= ie_incide_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
