CREATE OR REPLACE PROCEDURE pls_obter_interface_carteira ( cd_estabelecimento_p bigint, nm_usuario_p text, cd_interface_p INOUT bigint, cd_interface_reg_p INOUT bigint, ds_view_p INOUT text) AS $body$
DECLARE

 
nr_seq_emissor_w	bigint;
cd_interface_w		bigint;


BEGIN 
 
begin 
select	nr_seq_emissor 
into STRICT	nr_seq_emissor_w 
from	pls_parametros 
where	cd_estabelecimento	= cd_estabelecimento_p;
exception 
when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265828,'');
	--Favor informar o emissor padrão! (OPS - Gestão de Operadoras -> Parâmetros da OPS -> Contratos e Beneficiários). 
end;
 
begin 
select	cd_interface_envio 
into STRICT	cd_interface_w 
from	pls_emissor_carteira 
where	nr_sequencia	= nr_seq_emissor_w;
exception 
when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265829,'');
	--Favor informar a interface do emissor da carteirinha! (OPS - Cadastro de Regras -> Contratos e beneficiários -> Controle de identificação). 
end;
 
cd_interface_p	:= cd_interface_w;
 
if (cd_interface_w = 1856) then /* Exportação de cartão Vera Cruz */
 
	cd_interface_reg_p	:= 1;
	ds_view_p		:= 'pls_cartao_identificacao_v';
elsif (cd_interface_w = 1956) then /* Exportação de cartão Unimed São José Rio Preto */
 
	cd_interface_reg_p	:= 2;
	ds_view_p		:= 'pls_cartao_unimed_sjrp_v';
elsif (cd_interface_w = 1878) then /* Exportação de cartão Unimed Litoral */
 
	cd_interface_reg_p	:= 2;
	ds_view_p		:= 'pls_cartao_unimed_v';
elsif (cd_interface_w = 1971) then /* Exportação de cartão provisórios Unimed SJC */
 
	cd_interface_reg_p	:= 4;
	ds_view_p		:= 'pls_cart_provisoria_unimed_sjc';
elsif (cd_interface_w = 1992) then /* Exportação de cartão definitivos Unimed SJC */
 
	cd_interface_reg_p	:= 2;
	ds_view_p		:= 'pls_cart_definit_unimed_sjc_v';
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
