CREATE OR REPLACE PROCEDURE pls_obter_limite_reajuste_fx ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, qt_idade_limite_reaj_p INOUT bigint, qt_anos_limite_reaj_p INOUT bigint) AS $body$
DECLARE


qt_idade_limite_reaj_w	bigint;
qt_anos_limite_reaj_w	bigint;
nr_seq_contrato_w	pls_contrato.nr_sequencia%type;


BEGIN

select	coalesce(max(qt_idade_limite_reaj),0),
	coalesce(max(qt_anos_limite_reaj),0),
	max(nr_seq_contrato)
into STRICT	qt_idade_limite_reaj_w,
	qt_anos_limite_reaj_w,
	nr_seq_contrato_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (qt_idade_limite_reaj_w = 0) and (qt_anos_limite_reaj_w = 0) and (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
	select	coalesce(max(qt_idade_limite_reaj),0),
		coalesce(max(qt_anos_limite_reaj),0)
	into STRICT	qt_idade_limite_reaj_w,
		qt_anos_limite_reaj_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_w;
end if;

if (qt_idade_limite_reaj_w = 0) and (qt_anos_limite_reaj_w = 0) then
	select	coalesce(max(qt_idade_limite),0),
		coalesce(max(qt_tempo_limite),0)
	into STRICT	qt_idade_limite_reaj_w,
		qt_anos_limite_reaj_w
	from	pls_parametros
	where	cd_estabelecimento = cd_estabelecimento_p;
end if;

qt_idade_limite_reaj_p	:= qt_idade_limite_reaj_w;
qt_anos_limite_reaj_p	:= qt_anos_limite_reaj_w;

end;
