CREATE OR REPLACE PROCEDURE pls_obter_mat_restricao_data ( nr_seq_material_p bigint, dt_referencia_p timestamp, nr_seq_restricao_p INOUT bigint) AS $body$
BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_restricao_p
from	pls_material_restricao a
where	a.nr_seq_material	= nr_seq_material_p
and	dt_referencia_p	between coalesce(a.dt_inicio_vigencia,dt_referencia_p) and coalesce(a.dt_fim_vigencia,dt_referencia_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
