CREATE OR REPLACE PROCEDURE pls_obter_medico_vinculo_web (nr_crm_p text, nr_seq_prestador_p bigint, nm_medico_p INOUT text, cd_pessoa_fisica_p INOUT text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Retornar os dados do médico, e verificar se o mesmo está vinculado ao prestador
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
*/ds_retorno_w		varchar(60);
cd_pessoa_fisica_w	varchar(20);
qt_registros_w		bigint;


BEGIN

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	medico
where	nr_crm	= nr_crm_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	select  count(1)
	into STRICT	qt_registros_w
	from	pls_prestador_medico
	where	nr_seq_prestador = nr_seq_prestador_p
	and	cd_medico = cd_pessoa_fisica_w;

	if (qt_registros_w = 0) then
		select  count(1)
		into STRICT	qt_registros_w
		from	pls_prestador
		where	nr_sequencia = nr_seq_prestador_p
		and	cd_pessoa_fisica = cd_pessoa_fisica_w;

		if (qt_registros_w = 0) then
			cd_pessoa_fisica_w := null;
		end if;
	end if;
end if;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	select	max(nm_pessoa_fisica)
	into STRICT	ds_retorno_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;
end if;

nm_medico_p := ds_retorno_w;
cd_pessoa_fisica_p := cd_pessoa_fisica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
