CREATE OR REPLACE PROCEDURE pls_obter_nome_arquivo_remessa ( nr_seq_cobranca_p bigint, nm_usuario_p text, nm_arquivo_p INOUT text) AS $body$
DECLARE


nm_arquivo_w		pls_desc_empresa.nm_arquivo%type;
dt_remessa_retorno_w	cobranca_escritural.dt_remessa_retorno%type;


BEGIN

select	a.dt_remessa_retorno,
	b.nm_arquivo
into STRICT	dt_remessa_retorno_w,
	nm_arquivo_w
from	cobranca_escritural a,
	pls_desc_empresa b
where	b.nr_sequencia  = a.nr_seq_empresa
and	a.nr_sequencia	= nr_seq_cobranca_p;

nm_arquivo_w	:= replace(nm_arquivo_w,'@MMYYYY',to_char(dt_remessa_retorno_w,'MMYYYY'));
nm_arquivo_w	:= replace(nm_arquivo_w,'@YYYYMM',to_char(dt_remessa_retorno_w,'YYYYMM'));
nm_arquivo_w	:= replace(nm_arquivo_w,'@YYYYMMDD',to_char(dt_remessa_retorno_w,'YYYYMMDD'));
nm_arquivo_w	:= replace(nm_arquivo_w,'@YYMM',to_char(dt_remessa_retorno_w,'YYMM'));

nm_arquivo_p	:= nm_arquivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
