CREATE OR REPLACE PROCEDURE pls_obter_parametros_usuario ( nr_seq_segurado_p bigint, nr_seq_estipulante_p bigint, nr_seq_usu_prestador_p bigint, cd_estabelecimento_p bigint, nm_usuario_param_p text, vl_parametros_p INOUT text) AS $body$
DECLARE

				 
nr_seq_param_w		bigint;
vl_parametro_w 		varchar(255);
vl_param_aux_w		varchar(255);
nr_seq_contrato_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		coalesce(vl_parametro, vl_parametro_padrao) vl_parametro 
	from	pls_web_param_outorgante 
	/* Foi retirado esta linha porque o campo cd_estabelecimento não esta sendo utilizado na tabela e nas consistências */
 
	/*where	cd_estabelecimento = cd_estabelecimento_p*/
 
	order by cd_parametro;
	

BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_seq_param_w, 
	vl_parametro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	coalesce(max(vl_parametro), null) 
	into STRICT	vl_param_aux_w 
	from	pls_web_param_usuario 
	where	nr_seq_param = nr_seq_param_w 
	and	((nr_seq_segurado_p 	> 0 and nr_seq_segurado = nr_seq_segurado_p) 
	or (nr_seq_estipulante_p 	> 0 and nr_seq_usu_estipulante = nr_seq_estipulante_p) 
	or (nr_seq_usu_prestador_p > 0 and nr_seq_usu_prestador = nr_seq_usu_prestador_p) 
	or ((nm_usuario_param_p IS NOT NULL AND nm_usuario_param_p::text <> '') and upper(nm_usuario_param) = upper(nm_usuario_param_p )));
	 
	if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then 
		nr_seq_contrato_w := pls_obter_dados_segurado(nr_seq_segurado_p, 'NC');
	elsif (nr_seq_estipulante_p IS NOT NULL AND nr_seq_estipulante_p::text <> '') then 
		select	coalesce(max(nr_seq_contrato),null) 
		into STRICT	nr_seq_contrato_w 
		from	pls_estipulante_web 
		where	nr_sequencia = nr_seq_estipulante_p;
	end if;
	 
	if (vl_param_aux_w IS NOT NULL AND vl_param_aux_w::text <> '') then 
		vl_parametro_w := vl_param_aux_w;
	elsif (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then 
	 
		select	coalesce(max(vl_parametro), null) 
		into STRICT	vl_param_aux_w 
		from	pls_web_param_contrato 
		where	nr_seq_contrato = nr_seq_contrato_w 
		and	nr_seq_param 	= nr_seq_param_w;
		 
		if (vl_param_aux_w IS NOT NULL AND vl_param_aux_w::text <> '') then 
			vl_parametro_w := vl_param_aux_w;
		end if;
		 
	end if;
	 
	if (coalesce(vl_parametros_p::text, '') = '') then 
		vl_parametros_p := vl_parametro_w;
	else 
		vl_parametros_p := vl_parametros_p || '#@' || vl_parametro_w;
	end if;
	 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
