CREATE OR REPLACE PROCEDURE pls_obter_param_guia_medico ( cd_estabelecimento_web_p INOUT text, ds_chave_google_p INOUT text, ie_permite_td_cidades_p INOUT text, ie_permite_tp_guia_p INOUT text, ie_tipo_categoria_p INOUT text, ie_permite_produtos_p INOUT text, ie_tipo_pesq_medico_p INOUT text, ie_permite_indicacao_p INOUT text, ie_permite_vis_prod_p INOUT text, ie_mostra_link_p INOUT text, ds_link_operadora_p INOUT text, ie_permite_carater_atend_p INOUT text, ie_exibe_bairro_prest_p INOUT text, ie_tipo_prestador_consulta_p INOUT text, ie_permite_area_atuacao_p INOUT text, ie_obriga_tipo_guia_p INOUT text, ie_acred_rn405_p INOUT text, ie_tipo_prest_estab_p INOUT text) AS $body$
