 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

PLS_OBTER_PARAM_PADRAO_FUNCAO - Faz a busca por parâmetros cliente.
PLS_PARAMETRO_OPERADORA_WEB - Faz a busca pelos parâmetros em OPS - Gestão de Operadoras / Parâmetros OPS .

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_param_geral_w		pls_web_param_geral.nr_sequencia%type;
nr_seq_param_guia_medico_w	pls_web_param_guia_medico.nr_sequencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_param_geral_w
from	pls_web_param_geral;

select	max(nr_sequencia)
into STRICT	nr_seq_param_guia_medico_w
from	pls_web_param_guia_medico;

select	cd_estabelecimento
into STRICT	cd_estabelecimento_web_p
from	pls_web_param_geral
where	nr_sequencia	= nr_seq_param_geral_w;

select	coalesce(ie_categoria_guia_medico, 'A'),
	coalesce(ie_produto_guia_medico, 'N'),
	coalesce(ie_exibe_bairro_prestador,'S'),
	coalesce(ie_tipo_prestador_consulta,'S'),
	coalesce(ie_obriga_tipo_guia, 'N'),
	coalesce(ie_acred_rn405,'N'),
	coalesce(ie_tipo_prest_estab,'TP')
into STRICT	ie_tipo_categoria_p,
	ie_permite_produtos_p,
	ie_exibe_bairro_prest_p,
	ie_tipo_prestador_consulta_p,
	ie_obriga_tipo_guia_p,
	ie_acred_rn405_p,
	ie_tipo_prest_estab_p
from	pls_web_param_guia_medico
where	nr_sequencia	= nr_seq_param_guia_medico_w;

ds_chave_google_p := pls_obter_param_padrao_funcao(1,1256);
ie_permite_td_cidades_p := pls_obter_param_padrao_funcao(6,1256);
ie_permite_tp_guia_p := pls_obter_param_padrao_funcao(7,1256);
ie_tipo_pesq_medico_p := pls_obter_param_padrao_funcao(10,1256);
ie_permite_indicacao_p := pls_obter_param_padrao_funcao(2,1256);
ie_permite_vis_prod_p := pls_obter_param_padrao_funcao(9,1256);
ie_mostra_link_p := pls_obter_param_padrao_funcao(5,1256);
ie_permite_carater_atend_p := pls_obter_param_padrao_funcao(15,1256);
ie_permite_area_atuacao_p := pls_obter_param_padrao_funcao(16,1256);

if (ie_mostra_link_p = 'S') then
	ds_link_operadora_p := pls_obter_param_padrao_funcao(4, 1256);
end if;

end;
$body$
