CREATE OR REPLACE PROCEDURE pls_obter_preco_mat_forn_adic ( cd_estabelecimento_p bigint, nr_seq_material_p bigint, dt_vigencia_p timestamp, tx_ajuste_tab_p bigint, nr_seq_fornecedor_p bigint, vl_material_p INOUT bigint, dt_ult_vigencia_p INOUT timestamp) AS $body$
DECLARE


vl_preco_venda_w		double precision;
nr_seq_fornecedor_w		bigint;
dt_inicio_vigencia_w		timestamp;
dt_vigencia_w			timestamp;

c01 CURSOR FOR
	SELECT	/*+ USE_CONCAT */		vl_preco
	from	pls_material_fornec_preco x,
		pls_material_fornec w
	where	w.nr_sequencia		= x.nr_seq_mat_fornec
	and	w.nr_seq_fornecedor	= nr_seq_fornecedor_w
	and	dt_vigencia_w between trunc(x.dt_inicio_vigencia) and fim_dia(coalesce(x.dt_fim_vigencia,clock_timestamp()))
	and	exists (SELECT	1
			from	pls_material 	b
			where	b.nr_sequencia		= w.nr_seq_material
			and	b.nr_sequencia		= nr_seq_material_p
			and	b.cd_estabelecimento	= cd_estabelecimento_p)
	order by x.dt_inicio_vigencia;


BEGIN
dt_vigencia_w := trunc(dt_vigencia_p);

-- Obter a sequencia do prestador/fornecedor
select	max(w.nr_sequencia)
into STRICT	nr_seq_fornecedor_w
from	pls_prestador x,
	pls_fornecedor_material w
where	x.cd_cgc	= w.cd_cgc
and	x.nr_sequencia	= nr_seq_fornecedor_p;

open c01;
loop
fetch c01 into
	vl_preco_venda_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

vl_material_p		:= (coalesce(vl_preco_venda_w,0) * tx_ajuste_tab_p);
dt_ult_vigencia_p	:= coalesce(dt_inicio_vigencia_w,clock_timestamp());

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
