CREATE OR REPLACE PROCEDURE pls_obter_registros_info_pag ( nr_seq_contrato_p bigint , ds_erro_p INOUT text) AS $body$
DECLARE

		 
qt_registros_w			bigint;
nr_seq_pagador_w		bigint;
nm_pagador_w			varchar(255);
ds_erro_w			varchar(4000);

C01 CURSOR FOR 
	SELECT b.nr_sequencia, 
		substr(obter_nome_pf_pj(b.cd_pessoa_fisica,b.cd_cgc),1,255) 
	from	pls_contrato_pagador      b, 
		pls_contrato    		a 
	where  a.nr_sequencia    = nr_seq_contrato_p 
	and	b.nr_seq_contrato	= a.nr_sequencia;

BEGIN
 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_pagador_w, 
	nm_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	count(*) 
	into STRICT	qt_registros_w 
	from	pls_contrato_pagador_fin	c, 
		pls_contrato_pagador      b, 
		pls_contrato    		a 
	where  b.nr_seq_contrato    = nr_seq_contrato_p 
	and	c.nr_seq_pagador	= b.nr_sequencia 
	and	b.nr_sequencia		= nr_seq_pagador_w 
	and	b.nr_seq_contrato	= a.nr_sequencia;
	 
	if (qt_registros_w = 0) then 
		ds_erro_w := wheb_mensagem_pck.get_texto(280914, 'NR_SEQ_PAGADOR_P=' || nr_seq_pagador_w || ';NM_PAGADOR_P=' || nm_pagador_w);
	end if;
	end;
end loop;
close C01;
 
ds_erro_p	:= substr(ds_erro_w,1,100);
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
