CREATE OR REPLACE PROCEDURE pls_obter_regra_autor_pend ( ie_tipo_guia_p text, nr_seq_regra_p INOUT bigint) AS $body$
DECLARE


nr_seq_regra_pend_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_regra_autorizacao_pend	a
	where	a.ie_situacao	= 'A'
	and	coalesce(a.ie_tipo_guia,ie_tipo_guia_p)	= ie_tipo_guia_p
	order by
		coalesce(a.ie_tipo_guia,' ');


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_regra_pend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

nr_seq_regra_p	:= nr_seq_regra_pend_w;

end;
$body$
