CREATE OR REPLACE PROCEDURE pls_obter_regra_bloq_benef_web ( nr_seq_congenere_p bigint, ds_retorno_regra_bloq_p INOUT text, nr_seq_regra_bloq_p INOUT bigint, ie_bloq_alteracao_benef_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	verificar regra de bloqueio de cadastro de beneficiários eventuais.

OPS - Cadastro de Regras / OPS - Atendimento / Identificação beneficiário web, a opção 'Bloqueio beneficiário eventual'.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [X] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_mensagem_w			varchar(255);
nr_seq_regra_w			integer := 0;
ie_bloq_alteracao_benef_w		varchar(1) := 'N';


BEGIN
	if (pls_obter_se_controle_estab('RE') = 'S')then
		begin
			select 	max(a.nr_sequencia),
				b.ie_bloqueio_alteracao
			into STRICT	nr_seq_regra_w,
				ie_bloq_alteracao_benef_w
			from 	pls_cong_coop_bloq_web a,
				pls_regra_bloq_benef_web b
			where	a.nr_seq_congenere = nr_seq_congenere_p
			and	a.nr_seq_regra_bloq_web = b.nr_sequencia
			and	a.ie_situacao = 'A'
			and	b.ie_situacao = 'A'
			and	trunc(clock_timestamp()) between trunc(b.dt_inicio) and trunc(coalesce(b.dt_final,clock_timestamp()))
			and 	coalesce(b.cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
			group by b.ie_bloqueio_alteracao;

			if (coalesce(nr_seq_regra_w,0) > 0) then

				select 	a.ds_mensagem
				into STRICT	ds_mensagem_w
				from	pls_regra_bloq_benef_web a,
					pls_cong_coop_bloq_web b
				where 	b.nr_sequencia = nr_seq_regra_w
				and	a.nr_sequencia = b.nr_seq_regra_bloq_web
				and 	coalesce(a.cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;

				if (coalesce(ds_mensagem_w,'N') = 'N') then
					ds_mensagem_w := 'Não é permitido cadastrar e/ou atualizar os dados do beneficiário.';
				end if;
			end if;
		exception
		when others then
			nr_seq_regra_w 			:= null;
			ie_bloq_alteracao_benef_w 	:= null;
			ds_mensagem_w 			:= null;
		end;
	else
		begin
			select 	max(a.nr_sequencia),
				b.ie_bloqueio_alteracao
			into STRICT	nr_seq_regra_w,
				ie_bloq_alteracao_benef_w
			from 	pls_cong_coop_bloq_web a,
				pls_regra_bloq_benef_web b
			where	a.nr_seq_congenere = nr_seq_congenere_p
			and	a.nr_seq_regra_bloq_web = b.nr_sequencia
			and	a.ie_situacao = 'A'
			and	b.ie_situacao = 'A'
			and	trunc(clock_timestamp()) between trunc(b.dt_inicio) and trunc(coalesce(b.dt_final,clock_timestamp()))
			group by b.ie_bloqueio_alteracao;

			if (coalesce(nr_seq_regra_w,0) > 0) then

				select 	a.ds_mensagem
				into STRICT	ds_mensagem_w
				from	pls_regra_bloq_benef_web a,
					pls_cong_coop_bloq_web b
				where 	b.nr_sequencia = nr_seq_regra_w
				and	a.nr_sequencia = b.nr_seq_regra_bloq_web;

				if (coalesce(ds_mensagem_w,'N') = 'N') then
					ds_mensagem_w := 'Não é permitido cadastrar e/ou atualizar os dados do beneficiário.';
				end if;
			end if;
		exception
		when others then
			nr_seq_regra_w 			:= null;
			ie_bloq_alteracao_benef_w 	:= null;
			ds_mensagem_w 			:= null;
		end;
	end if;

ds_retorno_regra_bloq_p 	:= ds_mensagem_w;
nr_seq_regra_bloq_p 	:= nr_seq_regra_w;
ie_bloq_alteracao_benef_p 	:= ie_bloq_alteracao_benef_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obter_regra_bloq_benef_web ( nr_seq_congenere_p bigint, ds_retorno_regra_bloq_p INOUT text, nr_seq_regra_bloq_p INOUT bigint, ie_bloq_alteracao_benef_p INOUT text) FROM PUBLIC;

