CREATE OR REPLACE PROCEDURE pls_obter_regra_carencia ( ie_carater_solic_p text, nr_seq_plano_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_regra_carencia_p INOUT bigint) AS $body$
DECLARE

nr_seq_regra_w		bigint;
ie_segmentacao_w	varchar(3);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_lanc_carencia
	where	((coalesce(ie_segmentacao::text, '') = '') or (ie_segmentacao = ie_segmentacao_w))
	and	((coalesce(ie_carater_internacao::text, '') = '') or (ie_carater_internacao = ie_carater_solic_p))
	and	cd_estabelecimento = cd_estabelecimento_p
	and	ie_situacao = 'A'
	order by coalesce(ie_segmentacao,'A'),
		 coalesce(ie_carater_internacao,'A');

BEGIN

begin
	select	ie_segmentacao
	into STRICT	ie_segmentacao_w
	from	pls_plano
	where	nr_sequencia = nr_seq_plano_p;
exception
when others then
	ie_segmentacao_w := null;
end;
open C01;
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

if (coalesce(nr_seq_regra_w::text, '') = '') then
	nr_seq_regra_carencia_p := 0;
else
	nr_seq_regra_carencia_p := nr_seq_regra_w;
end if;

end;
$body$
