CREATE OR REPLACE PROCEDURE pls_obter_regra_conv_bras ( nr_seq_material_p bigint, dt_referencia_p timestamp, cd_estabelecimento_p bigint, qt_conversao_bras_p bigint, qt_conversao_atual_p bigint, qt_conversao_p INOUT bigint) AS $body$
DECLARE


nr_seq_regra_mat_uni_bra_w		pls_regra_mat_uni_sc_bras.nr_sequencia%type;
qt_conversao_fixo_w			pls_regra_mat_uni_sc_bras.qt_conversao_fixo%type;
ie_aplicar_conv_fed_sc_w		pls_regra_mat_uni_sc_bras.ie_aplicar_conv_fed_sc%type;
qt_conversao_w				bigint := null;
cd_material_ops_w			pls_material.cd_material_ops%type;
cd_unidade_medida_w			pls_material.cd_unidade_medida%type;


BEGIN

select	max(cd_material_ops),
	max(cd_unidade_medida)
into STRICT	cd_material_ops_w,
	cd_unidade_medida_w
from	pls_material
where	nr_sequencia = nr_seq_material_p;

select	max(nr_sequencia)
into STRICT	nr_seq_regra_mat_uni_bra_w
from	pls_regra_mat_uni_sc_bras
where	((nr_seq_material	= nr_seq_material_p) or (coalesce(nr_seq_material::text, '') = ''))
and	((cd_material_ops	= cd_material_ops_w) or (coalesce(cd_material_ops::text, '') = '') or (coalesce(coalesce(cd_material_ops, cd_material_ops_w)::text, '') = ''))
and	((cd_unidade_medida	= cd_unidade_medida_w) or (coalesce(cd_unidade_medida::text, '') = ''))
and	dt_referencia_p between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_referencia_p);

if (nr_seq_regra_mat_uni_bra_w IS NOT NULL AND nr_seq_regra_mat_uni_bra_w::text <> '') then
	select	qt_conversao_fixo,
		ie_aplicar_conv_fed_sc
	into STRICT	qt_conversao_fixo_w,
		ie_aplicar_conv_fed_sc_w
	from	PLS_REGRA_MAT_UNI_SC_BRAS
	where	nr_sequencia	= nr_seq_regra_mat_uni_bra_w;

	/*Aplicar conversão da Federação*/

	if (ie_aplicar_conv_fed_sc_w = 'FED') then
		qt_conversao_w	:= qt_conversao_bras_p;
	/*Aplicar conversão fixa*/

	elsif (ie_aplicar_conv_fed_sc_w = 'FX') then
		qt_conversao_w	:= qt_conversao_fixo_w;
	/*Não aplicar conversão*/

	elsif (ie_aplicar_conv_fed_sc_w = 'N') then
		qt_conversao_w	:= qt_conversao_atual_p;
	end if;
else
	qt_conversao_w	:= qt_conversao_atual_p;
end if;

qt_conversao_p	:= qt_conversao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
