CREATE OR REPLACE PROCEDURE pls_obter_regra_doc_fisico_fat ( cd_area_procedimento_p pls_regra_doc_fisico_fat.cd_area_procedimento%type, cd_especialidade_p pls_regra_doc_fisico_fat.cd_especialidade%type, cd_grupo_proc_p pls_regra_doc_fisico_fat.cd_grupo_proc%type, cd_procedimento_p pls_regra_doc_fisico_fat.cd_procedimento%type, ie_origem_proced_p pls_regra_doc_fisico_fat.ie_origem_proced%type, nr_seq_material_p pls_regra_doc_fisico_fat.nr_seq_material%type, nr_seq_grupo_material_p pls_regra_doc_fisico_fat.nr_seq_grupo_material%type, nr_seq_prestador_p pls_regra_doc_fisico_fat.nr_seq_prestador%type, ie_tipo_desp_proc_p pls_regra_doc_fisico_fat.ie_tipo_desp_proc%type, ie_tipo_despesa_mat_p pls_regra_doc_fisico_fat.ie_tipo_despesa_mat%type, ie_tipo_guia_p pls_regra_doc_fisico_fat.ie_tipo_guia%type, dt_referencia_p pls_regra_doc_fisico_fat.dt_inicio_vigencia%type, nr_seq_tipo_atendimento_p pls_regra_doc_fisico_fat.nr_seq_tipo_atendimento%type, nr_seq_regra_fat_p pls_regra_doc_fisico_fat.nr_seq_regra_fat%type, ie_regime_atendimento_p pls_regra_doc_fisico_fat.ie_regime_atendimento%type, ie_saude_ocupacional_p pls_regra_doc_fisico_fat.ie_saude_ocupacional%type, nr_seq_regra_p INOUT pls_regra_doc_fisico_fat.nr_sequencia%type) AS $body$
DECLARE

						
nr_seq_regra_w	pls_regra_doc_fisico_fat.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_regra_doc_fisico_fat a
	where	dt_referencia_p between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,dt_referencia_p)
	and	((a.cd_area_procedimento 	= cd_area_procedimento_p) 	or (coalesce(a.cd_area_procedimento::text, '') = ''))
	and	((a.cd_especialidade 		= cd_especialidade_p) 		or (coalesce(a.cd_especialidade::text, '') = ''))
	and	((a.cd_grupo_proc 		= cd_grupo_proc_p) 		or (coalesce(a.cd_grupo_proc::text, '') = ''))
	and	((a.cd_procedimento 		= cd_procedimento_p) 		or (coalesce(a.cd_procedimento::text, '') = ''))
	and	((a.ie_origem_proced 		= ie_origem_proced_p) 		or (coalesce(a.ie_origem_proced::text, '') = ''))
	and	((a.nr_seq_material 		= nr_seq_material_p) 		or (coalesce(a.nr_seq_material::text, '') = ''))
	and	((a.nr_seq_grupo_material 	= nr_seq_grupo_material_p) 	or (coalesce(a.nr_seq_grupo_material::text, '') = ''))
	and	((a.nr_seq_prestador 		= nr_seq_prestador_p) 		or (coalesce(a.nr_seq_prestador::text, '') = ''))
	and	((a.ie_tipo_desp_proc 		= ie_tipo_desp_proc_p) 		or (coalesce(a.ie_tipo_desp_proc::text, '') = ''))
	and	((a.ie_tipo_despesa_mat 	= ie_tipo_despesa_mat_p) 	or (coalesce(a.ie_tipo_despesa_mat::text, '') = ''))
	and	((a.ie_tipo_guia 		= ie_tipo_guia_p) 		or (coalesce(a.ie_tipo_guia::text, '') = ''))
	and	((a.nr_seq_tipo_atendimento	= nr_seq_tipo_atendimento_p)	or (coalesce(a.nr_seq_tipo_atendimento::text, '') = ''))
	and	((a.nr_seq_regra_fat		= nr_seq_regra_fat_p)		or (coalesce(a.nr_seq_regra_fat::text, '') = ''))
	and	((a.ie_regime_atendimento	= ie_regime_atendimento_p)	or (coalesce(a.ie_regime_atendimento::text, '') = ''))
	and	((a.ie_saude_ocupacional 	= ie_saude_ocupacional_p)	or (coalesce(a.ie_saude_ocupacional::text, '') = ''))
	order by
		coalesce(a.cd_procedimento,0) desc,
		coalesce(a.cd_grupo_proc,0) desc,
		coalesce(a.cd_especialidade,0) desc,
		coalesce(a.cd_area_procedimento,0) desc,
		coalesce(a.ie_tipo_desp_proc,' ') desc,
		coalesce(a.nr_seq_material,0) desc,
		coalesce(a.nr_seq_grupo_material,0) desc,
		coalesce(a.ie_tipo_despesa_mat,' ') desc,
		coalesce(a.ie_tipo_guia,' ') desc,
		coalesce(a.ie_origem_proced,0) desc,
		coalesce(a.nr_seq_prestador,0) desc,
		coalesce(a.nr_seq_tipo_atendimento,0) desc,
		coalesce(a.nr_seq_regra_fat,0) desc,
		coalesce(a.ie_regime_atendimento,' ') desc,
		coalesce(a.ie_saude_ocupacional,' ') desc;
		
BEGIN
-- verifica se existe alguma regra vigente
select	max(nr_sequencia)
into STRICT	nr_seq_regra_w
from	pls_regra_doc_fisico_fat
where	dt_referencia_p between dt_inicio_vigencia and coalesce(dt_fim_vigencia, dt_referencia_p);

-- so processa o cursor caso exista uma regra vigente
if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then

	for	r_c01_w in c01 loop

		nr_seq_regra_w := r_c01_w.nr_sequencia;
		-- o primeiro registro que retornar e o mais restritivo e deve sair do cursor
		exit;
	end loop;
end if;

nr_seq_regra_p	:= nr_seq_regra_w;

end;
