CREATE OR REPLACE PROCEDURE pls_obter_regra_inf_fin_pag ( nr_seq_forma_cobranca_p text, cd_banco_p bigint, nr_seq_classif_itens_p bigint, nr_seq_plano_p bigint, nr_seq_regra_p INOUT bigint) AS $body$
DECLARE


nr_seq_regra_w		pls_regra_portador_mens.nr_sequencia%type;
ie_tipo_contratacao_w	pls_plano.ie_tipo_contratacao%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_portador_mens a
	where (coalesce(a.dt_fim_vigencia::text, '') = '' or a.dt_fim_vigencia >= clock_timestamp())
	and (a.ie_tipo_contratacao = ie_tipo_contratacao_w or ie_tipo_contratacao = 'T')
	and	((a.cd_banco = cd_banco_p) or (coalesce(a.cd_banco::text, '') = ''))
	and	((a.nr_seq_forma_cobranca = nr_seq_forma_cobranca_p) or (coalesce(a.nr_seq_forma_cobranca::text, '') = ''))
	and	((a.nr_seq_classif_itens = nr_seq_classif_itens_p) or (coalesce(a.nr_seq_classif_itens::text, '') = ''))
	and	((exists (	SELECT	1
				from	pls_regra_portador_plano x
				where	x.nr_seq_regra	= a.nr_sequencia
				and	x.nr_seq_plano	= nr_seq_plano_p))
		or (not exists ( select	1
				from	pls_regra_portador_plano x
				where	x.nr_seq_regra	= a.nr_sequencia)))
	order by coalesce(nr_seq_classif_itens,0),
		coalesce(cd_banco,0),
		coalesce(nr_seq_forma_cobranca, '');


BEGIN

select	max(ie_tipo_contratacao)
into STRICT	ie_tipo_contratacao_w
from	pls_plano
where	nr_sequencia	= nr_seq_plano_p;

ie_tipo_contratacao_w	:= substr(ie_tipo_contratacao_w,1,1);

open C01;
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

nr_seq_regra_p	:= coalesce(nr_seq_regra_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
