CREATE OR REPLACE PROCEDURE pls_obter_regra_tit_pag_reemb ( ie_forma_pagamento_p text, dt_referencia_p timestamp, nr_seq_regra_p INOUT bigint) AS $body$
DECLARE


nr_seq_regra_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_tit_reembolso
	where	dt_referencia_p	between dt_inicio_vigencia and fim_dia(coalesce(dt_fim_vigencia,dt_referencia_p))
	and	((ie_forma_pagamento = coalesce(ie_forma_pagamento_p,ie_forma_pagamento)) or (coalesce(ie_forma_pagamento::text, '') = ''))
	order by
		coalesce(ie_forma_pagamento,' ');


