CREATE OR REPLACE PROCEDURE pls_obter_regra_tit_rec_negoc ( ie_regulamentacao_plano_p text, ie_status_pagador_p text, dt_referencia_p timestamp, nr_seq_regra_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_regra_classe_tit_neg	a
	where	trunc(dt_referencia_p,'dd') between trunc(a.dt_inicio_vigencia,'dd') and fim_dia(coalesce(a.dt_fim_vigencia,dt_referencia_p))
	and	((a.ie_regulamentacao_plano	= ie_regulamentacao_plano_p) or (coalesce(a.ie_regulamentacao_plano::text, '') = ''))
	and	((a.ie_status_pagador		= ie_status_pagador_p) or (a.ie_status_pagador = 'A'))
	order by
		coalesce(a.ie_status_pagador,'A'),
		coalesce(a.ie_regulamentacao_plano,' '),
		a.nr_sequencia;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_regra_p := nr_seq_regra_w;
	end;
end loop;
close C01;

end;
$body$
