CREATE OR REPLACE PROCEDURE pls_obter_restr_envio_fat_coop (nr_seq_cooperativa_p bigint, nr_seq_congenere_p bigint, ie_tipo_intercambio_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ie_permite_p INOUT text) AS $body$
DECLARE


ie_fatura_congenere_coop_w	varchar(1);

C01 CURSOR FOR
	SELECT	ie_permite
	from	pls_congenere_rec_fat
	where	coalesce(nr_seq_cooperativa,nr_seq_cooperativa_p)	= nr_seq_cooperativa_p
	and (ie_tipo_intercambio = ie_tipo_intercambio_p or ie_tipo_intercambio = 'A' or ie_tipo_intercambio_p = 'A')
	and	nr_seq_congenere = nr_seq_congenere_p;


BEGIN
begin
select	ie_fatura_congenere_coop
into STRICT	ie_fatura_congenere_coop_w
from	pls_congenere
where	nr_sequencia = nr_seq_congenere_p;
exception
when others then
	ie_fatura_congenere_coop_w := 'N';
end;

if (coalesce(ie_fatura_congenere_coop_w,'N') = 'S') then
	open C01;
	loop
	fetch C01 into
		ie_permite_p;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
else
	ie_permite_p := 'S';
end if;

end;
$body$
