SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_obter_ret_auditoria_estip ( nr_seq_auditoria_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter retorno da auditoria do estipulante, ao finalizar a mesma
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ie_tipo_guia_w			varchar(4);
ie_guia_internacao_w		varchar(4);
ie_guia_spsadt_w		varchar(4);
ie_guia_consulta_w		varchar(4);
nr_seq_grupo_w			bigint;
qt_grupo_w			bigint;
qt_item_pendente_w		bigint;
qt_item_aprovado_w		bigint;
qt_item_negado_w		bigint;
ie_status_item_analise_w	varchar(1) := null;
nr_seq_ordem_w			bigint;


BEGIN

select	count(1)
into STRICT	qt_grupo_w
from	pls_auditoria_grupo
where	nr_seq_auditoria = nr_seq_auditoria_p
and	coalesce(dt_liberacao::text, '') = '';

select	count(1)
into STRICT	qt_item_pendente_w
from	pls_auditoria_item
where	nr_seq_auditoria = nr_seq_auditoria_p
and	ie_status = 'P';

if (qt_item_pendente_w > 0) then
	ie_status_item_analise_w := '1';
else
	select	count(1)
	into STRICT	qt_item_negado_w
	from	pls_auditoria_item
	where	nr_seq_auditoria = nr_seq_auditoria_p
	and	ie_status = 'N';

	if (qt_item_negado_w > 0 ) then
		ie_status_item_analise_w := '3';
	else
		select	count(1)
		into STRICT	qt_item_aprovado_w
		from	pls_auditoria_item
		where	nr_seq_auditoria = nr_seq_auditoria_p
		and	ie_status = 'A';

		if (qt_item_aprovado_w > 0 ) then
			ie_status_item_analise_w := '2';
		end if;
	end if;
end if;


if (qt_grupo_w = 1) and (ie_status_item_analise_w IS NOT NULL AND ie_status_item_analise_w::text <> '') then
	select	ie_tipo_guia
	into STRICT	ie_tipo_guia_w
	from	pls_auditoria
	where	nr_sequencia	= nr_seq_auditoria_p;

	select	max(nr_seq_grupo_auditor)
	into STRICT	nr_seq_grupo_w
	from	pls_ret_auditoria_estip
	where	ie_situacao	= 'A'
	and	(((coalesce(ie_guia_internacao,'N')	= 'S')	and (ie_tipo_guia_w	= 1))
	or	((coalesce(ie_guia_prorrogacao_int,'N') = 'S') and (ie_tipo_guia_w = 8))
	or	((coalesce(ie_guia_spsadt,'N')	= 'S')	and (ie_tipo_guia_w	= 2))
	or	((coalesce(ie_guia_consulta,'N')	= 'S')	and (ie_tipo_guia_w	= 3)))
	and (coalesce(ie_status_item_analise::text, '') = '' or ie_status_item_analise = ie_status_item_analise_w)
	order by nr_sequencia;

	if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then
		select	max(nr_seq_ordem)
		into STRICT	nr_seq_ordem_w
		from	pls_auditoria_grupo
		where	nr_seq_auditoria	= nr_seq_auditoria_p;

		insert into pls_auditoria_grupo(nr_sequencia, nr_seq_auditoria, nr_seq_grupo,
			 dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			 nm_usuario_nrec, nr_seq_ordem, ie_status,
			 ie_manual)
		values (nextval('pls_auditoria_grupo_seq'), nr_seq_auditoria_p, nr_seq_grupo_w,
			 clock_timestamp(), nm_usuario_p, clock_timestamp(),
			 nm_usuario_p, (nr_seq_ordem_w + 1), 'U',
			 'N');
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obter_ret_auditoria_estip ( nr_seq_auditoria_p bigint, nm_usuario_p text) FROM PUBLIC;

