CREATE OR REPLACE PROCEDURE pls_obter_se_auditor_dif ( nr_seq_grupo_p bigint, nr_seq_analise_p bigint, nm_usuario_p text, nm_auditor_atual_p INOUT text ) AS $body$
DECLARE


/*
Diego - 05/07/2011 - OS 330395
Esta rotina possui a finalidade de obter se o grupo dentro da análise (passados por parâmetro) já possui um auditor que não é o usuário.
*/
ds_retorno_w	varchar(255);


BEGIN

select	max(nm_auditor_atual)
into STRICT	ds_retorno_w
from	pls_auditoria_conta_grupo
where	nr_sequencia 	 = (	SELECT	max(nr_sequencia)
				from	pls_auditoria_conta_grupo
				where	nr_seq_grupo 		= nr_seq_grupo_p
				and	nr_seq_analise		= nr_seq_analise_p
				and	coalesce(dt_liberacao::text, '') = '');

nm_auditor_atual_p := ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
