CREATE OR REPLACE PROCEDURE pls_obter_se_caren_plano_ant ( nr_seq_segurado_p bigint, nr_seq_plano_p bigint, nr_seq_segurado_ant_p bigint, nr_seq_prestador_p bigint, nr_seq_cooperativa_p bigint, ie_retorno_p INOUT text) AS $body$
DECLARE


ie_abrangencia_ant_w		varchar(10);
ie_abrangencia_atual_w		varchar(10);
nr_seq_plano_ant_w		bigint;
ie_area_abrangida_w		varchar(10);


BEGIN
ie_area_abrangida_w	:= 'S';

select	ie_abrangencia
into STRICT	ie_abrangencia_atual_w
from	pls_plano
where	nr_sequencia	= nr_seq_plano_p;

if (nr_seq_segurado_ant_p IS NOT NULL AND nr_seq_segurado_ant_p::text <> '') then
	select	a.ie_abrangencia,
		a.nr_sequencia
	into STRICT	ie_abrangencia_ant_w,
		nr_seq_plano_ant_w
	from	pls_segurado	b,
		pls_plano	a
	where	b.nr_seq_plano	= a.nr_sequencia
	and	b.nr_sequencia	= nr_seq_segurado_ant_p;
else
	select	max(b.ie_abrangencia),
		max(a.nr_seq_plano_ant)
	into STRICT	ie_abrangencia_ant_w,
		nr_seq_plano_ant_w
	from	pls_segurado_alt_plano a,
		pls_plano b
	where	b.nr_sequencia		= a.nr_seq_plano_ant
	and	a.nr_seq_segurado	= nr_seq_segurado_p
	and	a.ie_situacao 		= 'A'
	and	a.nr_seq_plano_atual	= nr_seq_plano_p;
end if;

if (ie_abrangencia_ant_w IS NOT NULL AND ie_abrangencia_ant_w::text <> '') then
	--Veriica a abrangencia anterior com a nova, nos seguntes casos

		--Estadual para nacional

		--Grupos de municipios para Estadual

		--Grupo de municipios para Nacional

		--Municipal para Grupo de municipios

		--Municipal para Estadual

		--Municipal para Nacional
	if (ie_abrangencia_ant_w = 'E' and ie_abrangencia_atual_w = 'N') or (ie_abrangencia_ant_w = 'GM' and ie_abrangencia_atual_w = 'E') or (ie_abrangencia_ant_w = 'GM' and ie_abrangencia_atual_w = 'N') or (ie_abrangencia_ant_w = 'M' and ie_abrangencia_atual_w = 'GM') or (ie_abrangencia_ant_w = 'M' and ie_abrangencia_atual_w = 'E') or (ie_abrangencia_ant_w = 'M' and ie_abrangencia_atual_w = 'N') then
		if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then
			ie_area_abrangida_w := pls_consistir_area_atuacao(nr_seq_plano_ant_w, null, nr_seq_prestador_p, null, ie_area_abrangida_w);
		elsif (nr_seq_cooperativa_p IS NOT NULL AND nr_seq_cooperativa_p::text <> '') then
			ie_area_abrangida_w := pls_consistir_area_atuacao(nr_seq_plano_ant_w, null, null, nr_seq_cooperativa_p, ie_area_abrangida_w);
		end if;
	end if;
end if;

ie_retorno_p	:= ie_area_abrangida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
