CREATE OR REPLACE PROCEDURE pls_obter_se_checar_sca ( nr_seq_sca_p bigint, ie_opcao_p text, nr_seq_vinculo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_retorno_p INOUT text) AS $body$
DECLARE





nr_seq_sca_w		bigint;
nr_sequencia_w		bigint;
qt_registro_w		bigint;
nr_seq_tabela_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_plano nr_seq_sca,
		nr_seq_tabela
	from	pls_sca_vinculo
	where	nr_seq_segurado_simul	=  nr_seq_vinculo_p
	and	nr_seq_plano	= nr_seq_sca_p
	and	ie_opcao_p =  'SP'
	
union

	SELECT	nr_seq_plano nr_seq_sca,
		nr_seq_tabela
	from	pls_sca_vinculo
	where	nr_seq_benef_proposta	 = nr_seq_vinculo_p
	and	nr_seq_plano	= nr_seq_sca_p
	and	ie_opcao_p = 'BP'
	
union

	select	nr_seq_plano nr_seq_sca,
		nr_seq_tabela
	from	pls_sca_vinculo
	where	nr_seq_segurado	=   nr_seq_vinculo_p
	and	nr_seq_plano	= nr_seq_sca_p
	and	ie_opcao_p = 'BE'
	
union

	select	nr_seq_plano nr_seq_sca,
		nr_seq_tabela
	from	pls_sca_regra_contrato
	where	nr_seq_contrato	=  nr_seq_vinculo_p
	and	nr_seq_plano	= nr_seq_sca_p
	and	ie_opcao_p = 'CO'
	
union

	select	nr_seq_plano_adic nr_seq_sca,
		nr_seq_tabela
	from	pls_plano_servico_adic
	where	nr_seq_plano	=  nr_seq_vinculo_p
	and	nr_seq_plano_adic	= nr_seq_sca_p
	and	ie_opcao_p = 'PL';



BEGIN

open C01;
loop
fetch C01 into
	nr_seq_sca_w,
	nr_seq_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_retorno_p	:= 'S';
	end;
end loop;
close C01;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
